    // Test Step 2: Custom Users Table Insert
    console.log('\nStep 4b: Testing custom users table insert...');

    try {
      const { data: customUser, error: customError } = await supabase
        .from('users')
        .insert([
          {
            id: authData.user.id, // Use the same ID from Supabase Auth
            email: testEmail,
