        ])
        .select('*');

      if (customError) {
        console.error('âŒ Custom users table insert failed:', customError);
        console.error('This explains why you see no users in the table!');
        console.error('Error details:', {
          message: customError.message,
          code: customError.code,
          details: customError.details,
          hint: customError.hint
        });
      } else {
        console.log('âœ… Custom users table insert successful!');
        console.log('Custom user created:', customUser);
      }
    } catch (insertError) {
      console.error('âŒ Custom table insert exception:', insertError);
    }

    console.log('\n5. Checking what\'s in the users table now...');
