  border-color: #4b5563;
  color: #f9fafb;
}

body.dark .pickup-input-container input:focus {
  background: #404040;
  border-color: #667eea;
  box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
}

.pickup-input-row .detect-location-btn {
  background: none;
  border: none;
  padding: 0;
  margin: 0;
  min-width: unset;
