  width: 24px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
}

.pickup-input-row .detect-location-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Ride Confirmation Card Styles */
.ride-confirmation-card {
  background: linear-gradient(135deg, #fff 0%, #f8f9fa 100%);
  border-radius: 20px;
  padding: 25px;
  margin: 30px auto;
