import React, { useState, useEffect } from 'react';
import { customAuth } from '../utils/customAuth';
import '../styles/CustomerAuth.css';

const CustomerAuth = ({ onLogin, onClose, onDriverLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [autoLoading, setAutoLoading] = useState(false);
  const [error, setError] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: ''
  });

  // Load saved credentials and attempt auto-login on component mount
  useEffect(() => {
    const checkAndAutoLogin = async () => {
      const savedCredentials = localStorage.getItem('bidcab_remember_me');
      if (savedCredentials) {
        try {
          const { email, password } = JSON.parse(savedCredentials);
          setFormData(prev => ({
            ...prev,
            email,
            password
          }));
          setRememberMe(true);

          // Attempt auto-login with saved credentials
          console.log('Attempting auto-login with saved credentials...');
          setLoading(true);

          try {
            const authResult = await customAuth.verifyCredentials(email, password);

            if (!authResult.success) {
              console.log('Auto-login failed, credentials may be invalid:', authResult.error);
              // Remove invalid saved credentials
              localStorage.removeItem('bidcab_remember_me');
              setRememberMe(false);
              setFormData(prev => ({
                ...prev,
                password: '' // Clear password but keep email
              }));
            } else {
              // Auto-login successful
              console.log('Auto-login successful!');
              const user = authResult.user;

              const customerInfo = {
                uid: user.id,
                email: user.email,
                name: user.full_name || email.split('@')[0],
                phone: user.phone || '',
                type: user.user_type || 'customer'
              };

              // Generate a session token (simplified for demo)
              const sessionToken = btoa(`${user.id}:${Date.now()}:${Math.random()}`);
              localStorage.setItem('customerToken', sessionToken);
              localStorage.setItem('customerData', JSON.stringify(customerInfo));

              // Refresh saved credentials with successful login
              localStorage.setItem('bidcab_remember_me', JSON.stringify({
                email,
                password
              }));

              if (onLogin) onLogin(customerInfo);
              if (onClose) onClose();
              return; // Exit early on successful auto-login
            }
          } catch (autoLoginError) {
            console.log('Auto-login error:', autoLoginError.message);
            // Remove invalid saved credentials
            localStorage.removeItem('bidcab_remember_me');
            setRememberMe(false);
            setFormData(prev => ({
              ...prev,
              password: '' // Clear password but keep email
            }));
          }

          setLoading(false);
        } catch (error) {
          console.error('Error loading saved credentials:', error);
          // Remove corrupted saved credentials
          localStorage.removeItem('bidcab_remember_me');
        }
      }
    };

    checkAndAutoLogin();
  }, [onLogin, onClose]);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const validateForm = () => {
    if (!formData.email || !formData.password) {
      setError('Email and password are required');
      return false;
    }

    if (!isLogin) {
      if (!formData.name || !formData.phone) {
        setError('All fields are required for registration');
        return false;
      }
      if (formData.password.length < 6) {
        setError('Password must be at least 6 characters');
        return false;
      }
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    setError('');

    try {
      if (isLogin) {
        // Login existing customer using custom auth
        const authResult = await customAuth.verifyCredentials(formData.email, formData.password);

        if (!authResult.success) {
          throw new Error(authResult.error);
        }

        const user = authResult.user;

        const customerInfo = {
          uid: user.id,
          email: user.email,
          name: user.full_name || formData.email.split('@')[0],
          phone: user.phone || '',
          type: user.user_type || 'customer'
        };

        // Generate a session token (simplified for demo)
        const sessionToken = btoa(`${user.id}:${Date.now()}:${Math.random()}`);
        localStorage.setItem('customerToken', sessionToken);
        localStorage.setItem('customerData', JSON.stringify(customerInfo));

        // Save credentials if Remember Me is checked
        if (rememberMe) {
          localStorage.setItem('bidcab_remember_me', JSON.stringify({
            email: formData.email,
            password: formData.password
          }));
        } else {
          localStorage.removeItem('bidcab_remember_me');
        }

        if (onLogin) onLogin(customerInfo);
        if (onClose) onClose();

      } else {
        // Register new customer using Supabase Auth
        const registrationData = {
          email: formData.email,
          password: formData.password,
          full_name: formData.name,
          phone: formData.phone,
          user_type: 'customer'
        };

        const authResult = await customAuth.register(registrationData);

        if (!authResult.success) {
          throw new Error(authResult.error);
        }

        const user = authResult.user;

        // Check if email confirmation is required
        if (!user.is_verified && authResult.session === null) {
          // Email confirmation required
          setError('Registration successful! Please check your email and click the confirmation link to complete your account setup.');
          setIsLogin(true); // Switch to login mode
          setFormData(prev => ({ ...prev, password: '' })); // Clear password
          return;
        }

        // User is verified or confirmation not required, proceed with login
        const customerInfo = {
          uid: user.id,
          email: user.email,
          name: user.full_name,
          phone: user.phone,
          type: user.user_type
        };

        // Generate a session token (simplified for demo)
        const sessionToken = btoa(`${user.id}:${Date.now()}:${Math.random()}`);
        localStorage.setItem('customerToken', sessionToken);
        localStorage.setItem('customerData', JSON.stringify(customerInfo));

        if (onLogin) onLogin(customerInfo);
        if (onClose) onClose();

        // Show success message for registration
        alert('Registration successful! You can now book rides.');
      }
    } catch (error) {
      console.error('ğŸš¨ Authentication error:', error);
      console.error('ğŸš¨ Error stack:', error.stack);

      const message = error.message || 'Authentication failed';

      // Handle specific error messages
      if (message.includes('Invalid login credentials') || message.includes('Invalid email or password')) {
        setError('Invalid email or password');
      } else if (message.includes('Email not confirmed') || message.includes('email_not_confirmed')) {
        setError('Please check your email and click the confirmation link before logging in.');
      } else if (message.includes('User already registered') || message.includes('already registered')) {
        setError('Email is already registered');
      } else if (message.includes('Password should be at least') || message.includes('Password must be at least')) {
        setError('Password must be at least 6 characters');
      } else if (message.includes('Invalid email') || message.includes('email_address_invalid')) {
        setError('Please enter a valid email address');
      } else if (message.includes('Password hashing failed')) {
        // This should not happen with the new Supabase Auth, but just in case
        setError('Registration failed. Please try again.');
        console.error('âŒ CRITICAL: Old password hashing code was somehow called!');
      } else {
        // Show the actual error message for debugging
        setError(message);
        console.error('ğŸ” Unhandled error message:', message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="customer-auth-overlay">
      <div className="customer-auth-modal">
        <div className="auth-header">
          <h2>ğŸš• {isLogin ? 'Login to BidCab' : 'Join BidCab'}</h2>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>

        {loading && !error && (
          <div className="auto-login-message">
            <span className="auto-login-icon">ğŸ”</span>
            <span>Signing you in automatically...</span>
          </div>
        )}

        {error && (
          <div className="error-message">
            <span className="error-icon">âš ï¸</span>
            <span>{error}</span>
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="email">ğŸ“§ Email Address</label>
            <div className="input-wrapper">
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Enter your email address"
                className="form-input"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password">ğŸ”’ Password</label>
            <div className="input-wrapper password-wrapper">
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="Enter your password"
                className="form-input"
                required
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
                tabIndex={-1}
              >
                {showPassword ? 'ğŸ™ˆ' : 'ğŸ‘ï¸'}
              </button>
            </div>
          </div>

          {!isLogin && (
            <>
              <div className="form-group">
                <label htmlFor="name">ğŸ‘¤ Full Name</label>
                <div className="input-wrapper">
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    placeholder="Enter your full name"
                    className="form-input"
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="phone">ğŸ“± Phone Number</label>
                <div className="input-wrapper">
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    placeholder="Enter your phone number"
                    className="form-input"
                    required
                  />
                </div>
              </div>
            </>
          )}

          {isLogin && (
            <div className="form-options">
              <label className="checkbox-wrapper">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                  className="checkbox-input"
                />
                <span className="checkbox-custom"></span>
                <span className="checkbox-label">Remember me</span>
              </label>
              <button type="button" className="forgot-password-btn">
                Forgot Password?
              </button>
            </div>
          )}

          <button type="submit" className="auth-btn" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner">âŸ³</span>
                {isLogin ? 'Signing In...' : 'Creating Account...'}
              </>
            ) : (
              <>
                <span className="btn-icon">ğŸš•</span>
                {isLogin ? 'Login' : 'Create Account'}
              </>
            )}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              type="button" 
              className="toggle-mode-btn" 
              onClick={() => {
                setIsLogin(!isLogin);
                setError('');
                setFormData({
                  email: '',
                  password: '',
                  name: '',
                  phone: ''
                });
              }}
            >
              {isLogin ? 'Sign up here' : 'Login here'}
            </button>
          </p>

          <p className="driver-link">
            Are you a driver? 
            <button 
              type="button" 
              className="driver-btn" 
              onClick={onDriverLogin}
            >
              Driver Login
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default CustomerAuth;

src/components/DriverBidNotification.js.content:
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabaseDB } from '../utils/supabaseService';

const DriverBidNotification = ({ driverData, onRideConfirmed }) => {
  const navigate = useNavigate();
  const [confirmedRide, setConfirmedRide] = useState(null);
  const [otpInput, setOtpInput] = useState('');
  const [otpError, setOtpError] = useState('');
  const [startingRide, setStartingRide] = useState(false);
  const [showNotification, setShowNotification] = useState(false);

  const driver = driverData || JSON.parse(localStorage.getItem('driverData') || '{}');

  // Check for bid acceptance every 3 seconds
  useEffect(() => {
    const checkForAcceptedBid = async () => {
      try {
        // Try database first
        try {
          const { data: acceptedRides, error } = await supabaseDB.bookings.getAll();

          if (!error && acceptedRides && acceptedRides.length > 0) {
            // Find rides confirmed for this driver
            const myConfirmedRide = acceptedRides.find(ride => 
              (ride.selected_driver_id === driver.id || 
               ride.selected_driver_id === driver.uid ||
               ride.driver_id === driver.id ||
               ride.driver_id === driver.uid) &&
              ride.status === 'confirmed' &&
              !ride.started_at // Not started yet
            );

            if (myConfirmedRide && !confirmedRide) {
              console.log('ğŸ‰ Customer accepted your bid!', myConfirmedRide);
              setConfirmedRide(myConfirmedRide);
              setShowNotification(true);

              // Play notification sound if available
              try {
                new Audio('/notification.mp3').play().catch(() => {});
              } catch (e) {}

              return;
            }
          }
        } catch (dbError) {
          console.log('Database check failed, checking localStorage...');
        }

        // Fallback: Check localStorage for accepted bookings
        const acceptedBooking = JSON.parse(localStorage.getItem('acceptedBooking') || '{}');
        const confirmedBooking = JSON.parse(localStorage.getItem('confirmedBooking') || '{}');

        const bookingToCheck = confirmedBooking.id ? confirmedBooking : acceptedBooking;

        if (bookingToCheck.selected_driver_id === (driver.id || driver.uid) && 
            bookingToCheck.status === 'confirmed' && 
            !confirmedRide) {

          console.log('ğŸ‰ Customer accepted your bid! (localStorage)', bookingToCheck);
          setConfirmedRide(bookingToCheck);
          setShowNotification(true);

          // Play notification sound if available
          try {
            new Audio('/notification.mp3').play().catch(() => {});
          } catch (e) {}
        }

      } catch (error) {
        console.error('Error checking for accepted bids:', error);
      }
    };

    // Check immediately and then every 3 seconds
    checkForAcceptedBid();
    const interval = setInterval(checkForAcceptedBid, 3000);

    return () => clearInterval(interval);
  }, [driver.id, driver.uid, confirmedRide]);

  const handleOtpChange = (value) => {
    setOtpInput(value);
    if (otpError) {
      setOtpError('');
    }
  };

  const startRide = async () => {
    if (!otpInput) {
      setOtpError('Please enter the customer\'s OTP');
      return;
    }

    if (otpInput.length !== 4) {
      setOtpError('OTP must be exactly 4 digits');
      return;
    }

    if (otpInput !== confirmedRide.otp) {
      setOtpError('Invalid OTP. Please ask the customer for the correct 4-digit code.');
      return;
    }

    setStartingRide(true);

    try {
      // Update ride status to in_progress
      let rideStarted = false;

      try {
        await supabaseDB.bookings.update(confirmedRide.id, {
          status: 'in_progress',
          started_at: new Date().toISOString()
        });
        rideStarted = true;
        console.log('âœ… Ride started in database');
      } catch (dbError) {
        // Fallback: Update localStorage
        const startedRide = {
          ...confirmedRide,
          status: 'in_progress',
          started_at: new Date().toISOString()
        };

        localStorage.setItem('activeRide', JSON.stringify(startedRide));
        localStorage.setItem(`ride_${confirmedRide.id}`, JSON.stringify(startedRide));

        // Remove from confirmed bookings
        localStorage.removeItem('confirmedBooking');
        localStorage.removeItem('acceptedBooking');

        rideStarted = true;
        console.log('âœ… Ride started in fallback mode');
      }

      if (rideStarted) {
        alert(`ğŸ‰ Ride Started Successfully!\n\nCustomer: ${confirmedRide.customer_name || confirmedRide.customerName}\nDestination: ${confirmedRide.drop_address || confirmedRide.drop}`);

        // Clear notification
        setShowNotification(false);
        setConfirmedRide(null);
        setOtpInput('');

        // Notify parent component
        if (onRideConfirmed) {
          onRideConfirmed(confirmedRide);
        }

        // Navigate to active rides page
        navigate('/driver/active-rides');
      }

    } catch (error) {
      console.error('Error starting ride:', error);
      setOtpError('Failed to start ride. Please try again.');
    } finally {
      setStartingRide(false);
    }
  };

  const dismissNotification = () => {
    setShowNotification(false);
    setConfirmedRide(null);
    setOtpInput('');
    setOtpError('');
  };

  if (!showNotification || !confirmedRide) {
    return null;
  }

  return (
    <div className="driver-notification-overlay">
      <div className="driver-notification-modal">
        <div className="notification-header">
          <div className="success-icon">ğŸ‰</div>
          <h2>Ride Confirmed!</h2>
          <p>Customer accepted your bid</p>
        </div>

        <div className="ride-details">
          <div className="customer-info">
            <h3>ğŸ‘¤ Customer: {confirmedRide.customer_name || confirmedRide.customerName || 'Customer'}</h3>
            {(confirmedRide.customer_phone || confirmedRide.customerPhone) && (
              <p>ğŸ“ {confirmedRide.customer_phone || confirmedRide.customerPhone}</p>
            )}
          </div>

          <div className="route-info">
            <div className="route-item">
              <span className="icon">ğŸ“</span>
              <div>
                <div className="label">Pickup</div>
                <div className="address">{confirmedRide.pickup_address || confirmedRide.pickup || 'Pickup Location'}</div>
              </div>
            </div>
            <div className="route-arrow">â†“</div>
            <div className="route-item">
              <span className="icon">ğŸ</span>
              <div>
                <div className="label">Drop</div>
                <div className="address">{confirmedRide.drop_address || confirmedRide.drop || 'Drop Location'}</div>
              </div>
            </div>
          </div>

          <div className="fare-info">
            <span className="label">Fare:</span>
            <span className="amount">â‚¹{confirmedRide.final_fare || confirmedRide.price || confirmedRide.estimated_fare}</span>
          </div>
        </div>

        <div className="otp-section">
          <h3>ğŸ” Enter Customer's OTP to Start Ride</h3>
          <p className="otp-instruction">Ask the customer for their 4-digit OTP code</p>

          <div className="otp-input-group">
            <input
              type="text"
              placeholder="Enter 4-digit OTP"
              value={otpInput}
              onChange={(e) => handleOtpChange(e.target.value)}
              maxLength={4}
              pattern="[0-9]{4}"
              className={`otp-input ${otpError ? 'error' : ''}`}
              autoFocus
            />
            <button 
              className="start-ride-btn" 
              onClick={startRide}
              disabled={startingRide || !otpInput || otpInput.length !== 4}
            >
              {startingRide ? (
                <>âŸ³ Starting...</>
              ) : (
                <>ğŸš— Start Ride</>
              )}
            </button>
          </div>

          {otpError && (
            <div className="otp-error">
              âš ï¸ {otpError}
            </div>
          )}
        </div>

        <div className="notification-actions">
          <button className="dismiss-btn" onClick={dismissNotification}>
            âœ• Dismiss
          </button>
        </div>
      </div>

      {/* Overlay styles */}
      <style jsx>{`
        .driver-notification-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.8);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 10000;
          animation: fadeIn 0.3s ease-out;
        }

        .driver-notification-modal {
          background: white;
          border-radius: 16px;
          padding: 32px;
          max-width: 500px;
          width: 90%;
          max-height: 90vh;
          overflow-y: auto;
          box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
          animation: slideIn 0.3s ease-out;
        }

        .notification-header {
          text-align: center;
          margin-bottom: 24px;
        }

        .success-icon {
          font-size: 3rem;
          margin-bottom: 8px;
        }

        .notification-header h2 {
          color: #10b981;
          margin: 0 0 8px 0;
          font-size: 1.8rem;
        }

        .notification-header p {
          color: #6b7280;
          margin: 0;
        }

        .ride-details {
          background: #f9fafb;
          border-radius: 12px;
          padding: 20px;
          margin-bottom: 24px;
        }

        .customer-info h3 {
          color: #1f2937;
          margin: 0 0 8px 0;
        }

        .customer-info p {
          color: #3b82f6;
          margin: 0;
        }

        .route-info {
          margin: 16px 0;
        }

        .route-item {
          display: flex;
          align-items: center;
          gap: 12px;
          margin: 8px 0;
        }

        .route-item .icon {
          font-size: 1.2rem;
        }

        .route-item .label {
          font-size: 0.9rem;
          color: #6b7280;
          margin-bottom: 4px;
        }

        .route-item .address {
          font-weight: 500;
          color: #1f2937;
        }

        .route-arrow {
          text-align: center;
          color: #6b7280;
          margin: 4px 0;
          font-size: 1.2rem;
        }

        .fare-info {
          display: flex;
          justify-content: space-between;
