src/components/DriverLogin.js.content:
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { supabaseAuth, supabaseDB } from '../utils/supabaseService';

const DriverLogin = ({ onLogin }) => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    vehicleType: 'sedan',
    vehicleNumber: '',
    licenseNumber: ''
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const validateForm = () => {
    if (!formData.email || !formData.password) {
      setError('Email and password are required');
      return false;
    }

    if (!isLogin) {
      if (!formData.name || !formData.phone || !formData.vehicleNumber || !formData.licenseNumber) {
        setError('All fields are required for registration');
        return false;
      }
      if (formData.password.length < 6) {
        setError('Password must be at least 6 characters');
        return false;
      }
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    setError('');

    try {
      if (isLogin) {
        // Login existing driver
        const { data: authData, error: authError } = await supabaseAuth.signIn(formData.email, formData.password);

        if (authError) {
          throw new Error(authError.message);
        }

        const user = authData.user;

        // Check if user is a driver by finding them in the drivers table
        const { data: drivers, error: driverError } = await supabaseDB.drivers.getAll();
        if (driverError) {
          throw new Error('Failed to check driver status');
        }

        const driverRecord = drivers.find(d => d.email === user.email);
        if (!driverRecord) {
          setError('This account is not registered as a driver');
          await supabaseAuth.signOut();
          return;
        }

        const driverInfo = {
          uid: user.id,
          id: driverRecord.id,
          email: user.email,
          name: driverRecord.name,
          phone: driverRecord.phone,
          vehicleType: driverRecord.vehicle_type,
          vehicleNumber: driverRecord.vehicle_number,
          rating: driverRecord.rating || 5.0,
          totalRides: driverRecord.total_rides || 0,
          isOnline: false,
          currentLocation: null,
          available: driverRecord.available
        };

        localStorage.setItem('driverToken', authData.session.access_token);
        localStorage.setItem('driverData', JSON.stringify(driverInfo));

        if (onLogin) onLogin(driverInfo);
        navigate('/driver/dashboard');
      } else {
        // Register new driver
        const { data: authData, error: authError } = await supabaseAuth.signUp(
          formData.email, 
          formData.password,
          {
            full_name: formData.name,
            phone: formData.phone
          }
        );

        if (authError) {
          throw new Error(authError.message);
        }

        // Create driver record in drivers table
        const driverData = {
          name: formData.name,
          phone: formData.phone,
          email: formData.email,
          vehicle_type: formData.vehicleType,
          vehicle_number: formData.vehicleNumber,
          vehicle_model: '', // Can be added later
          license_number: formData.licenseNumber,
          rating: 5.0,
          total_rides: 0,
          available: false, // Start offline
          location: null
        };

        let driverRecord = null;
        let driverId = null;

        // Try to create driver record in database
        try {
          const { data: dbDriverRecord, error: driverError } = await supabaseDB.drivers.add(driverData);

          if (driverError) {
            console.warn('Supabase driver creation failed:', driverError);
            throw new Error('Database not available');
          }

          driverRecord = dbDriverRecord[0];
          driverId = driverRecord.id;
          console.log('âœ… Driver profile created in database:', driverId);
        } catch (dbError) {
          console.log('Database unavailable, using fallback driver registration...');

          // Fallback: Create a temporary driver ID and store locally
          driverId = 'driver_' + Date.now();
          driverRecord = {
            id: driverId,
            ...driverData,
            created_at: new Date().toISOString()
          };

          // Store driver data in localStorage as backup
          const existingDrivers = JSON.parse(localStorage.getItem('fallbackDrivers') || '[]');
          existingDrivers.push(driverRecord);
          localStorage.setItem('fallbackDrivers', JSON.stringify(existingDrivers));

          console.log('âœ… Driver profile created in fallback mode:', driverId);
        }

        const driverInfo = {
          uid: authData.user?.id,
          id: driverId,
          email: formData.email,
          name: formData.name,
          phone: formData.phone,
          vehicleType: formData.vehicleType,
          vehicleNumber: formData.vehicleNumber,
          licenseNumber: formData.licenseNumber,
          rating: 5.0,
          totalRides: 0,
          isOnline: false,
          currentLocation: null,
          available: false
        };

        localStorage.setItem('driverToken', authData.session?.access_token || 'temp-token');
        localStorage.setItem('driverData', JSON.stringify(driverInfo));

        if (onLogin) onLogin(driverInfo);
        navigate('/driver/dashboard');
      }
    } catch (error) {
      console.error('Authentication error:', error);

      // Handle Supabase-specific error messages
      const message = error.message || 'Authentication failed';
      if (message.includes('Invalid login credentials') || message.includes('Invalid email or password')) {
        setError('Invalid email or password');
      } else if (message.includes('User already registered')) {
        setError('Email is already registered');
      } else if (message.includes('Password should be at least')) {
        setError('Password must be at least 6 characters');
      } else if (message.includes('Invalid email')) {
        setError('Invalid email address');
      } else {
        setError(message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="driver-login-container">
      <div className="driver-login-card">
        <div className="driver-login-header">
          <h2>ğŸš— Driver {isLogin ? 'Login' : 'Registration'}</h2>
          <p>Join BidCab as a professional driver</p>
        </div>

        {error && (
          <div className="error-message">
            <span className="error-icon">âš ï¸</span>
            <span>{error}</span>
          </div>
        )}

        <form onSubmit={handleLogin} className="driver-login-form">
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              required
            />
          </div>

          {!isLogin && (
            <>
              <div className="form-group">
                <label htmlFor="name">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Enter your full name"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  placeholder="Enter your phone number"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="vehicleType">Vehicle Type</label>
                <select
                  id="vehicleType"
                  name="vehicleType"
                  value={formData.vehicleType}
                  onChange={handleInputChange}
                  required
                >
                  <option value="sedan">Sedan</option>
                  <option value="hatchback">Hatchback</option>
                  <option value="suv">SUV</option>
                  <option value="mini">Mini</option>
                  <option value="luxury">Luxury</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="vehicleNumber">Vehicle Number</label>
                <input
                  type="text"
                  id="vehicleNumber"
                  name="vehicleNumber"
                  value={formData.vehicleNumber}
                  onChange={handleInputChange}
                  placeholder="Enter vehicle registration number"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="licenseNumber">Driving License Number</label>
                <input
                  type="text"
                  id="licenseNumber"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={handleInputChange}
                  placeholder="Enter driving license number"
                  required
                />
              </div>
            </>
          )}

          <button type="submit" className="driver-login-btn" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner">âŸ³</span>
                {isLogin ? 'Signing In...' : 'Registering...'}
              </>
            ) : (
              <>
                <span className="btn-icon">ğŸš—</span>
                {isLogin ? 'Login as Driver' : 'Register as Driver'}
              </>
            )}
          </button>
        </form>

        <div className="driver-login-footer">
          <p>
            {isLogin ? "New driver? " : "Already have an account? "}
            <button 
              type="button" 
              className="toggle-mode-btn" 
              onClick={() => {
                setIsLogin(!isLogin);
                setError('');
                setFormData({
                  email: '',
                  password: '',
                  name: '',
                  phone: '',
                  vehicleType: 'sedan',
                  vehicleNumber: '',
                  licenseNumber: ''
                });
              }}
            >
              {isLogin ? 'Register here' : 'Login here'}
            </button>
          </p>

          <p className="customer-link">
            Are you a customer? 
            <button 
              type="button" 
              className="customer-btn" 
              onClick={() => window.location.pathname = '/'}
            >
              Book a Ride
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default DriverLogin;

src/components/DriverLoginFixed.js.content:
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { supabaseAuth, supabaseDB } from '../utils/supabaseService';

const DriverLoginFixed = ({ onLogin }) => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    vehicleType: 'sedan',
    vehicleNumber: '',
    vehicleModel: '',
    licenseNumber: ''
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const validateForm = () => {
    if (!formData.email || !formData.password) {
      setError('Email and password are required');
      return false;
    }

    if (!isLogin) {
      if (!formData.name || !formData.phone || !formData.vehicleNumber) {
        setError('Name, phone, and vehicle number are required for registration');
        return false;
      }
      if (formData.password.length < 6) {
        setError('Password must be at least 6 characters');
        return false;
      }
    }
    return true;
  };

  const createDriverRecord = async (authUser, retryCount = 0) => {
    const driverData = {
      name: formData.name,
      phone: formData.phone,
      email: formData.email,
      vehicle_type: formData.vehicleType,
      vehicle_number: formData.vehicleNumber,
      rating: 5.0,
      total_rides: 0,
      available: false, // Start offline
      location: null,
      earnings: 0
    };

    // Only add license_number if the field exists in the database
    if (formData.licenseNumber) {
      driverData.license_number = formData.licenseNumber;
    }

    try {
      console.log('ğŸ”„ Attempting to create driver record...');
      const { data: dbDriverRecord, error: driverError } = await supabaseDB.drivers.add(driverData);

      if (driverError) {
        console.error('âŒ Driver record creation failed:', driverError);

        // If RLS policy error, try a different approach
        if (driverError.message?.includes('row-level security') || driverError.code === '42501') {
          if (retryCount < 2) {
            console.log('ğŸ”„ Retrying driver creation with auth context...');
            // Wait a moment for auth context to be established
            await new Promise(resolve => setTimeout(resolve, 1000));
            return createDriverRecord(authUser, retryCount + 1);
          }
          throw new Error('Database permission error. Please ensure RLS policies are correctly configured for driver registration.');
        }
        throw new Error(driverError.message || 'Failed to create driver profile');
      }

      return dbDriverRecord[0];
    } catch (error) {
      console.error('ğŸ’¥ Driver record creation error:', error);
      throw error;
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    setError('');

    try {
      if (isLogin) {
        // Login existing driver
        console.log('ğŸ” Attempting driver login...');
        const { data: authData, error: authError } = await supabaseAuth.signIn(formData.email, formData.password);

        if (authError) {
          throw new Error(authError.message);
        }

        const user = authData.user;
        console.log('âœ… Auth login successful for:', user.email);

        // Check if user is a driver by finding them in the drivers table
        const { data: drivers, error: driverError } = await supabaseDB.drivers.getAll();
        if (driverError) {
          console.error('âŒ Error fetching drivers:', driverError);
          throw new Error('Failed to verify driver status');
        }

        const driverRecord = drivers.find(d => d.email === user.email);
        if (!driverRecord) {
          console.log('âŒ Driver record not found for email:', user.email);
          console.log('ğŸ“Š Available drivers:', drivers.map(d => d.email));
          setError('This account is not registered as a driver. Please register first.');
          await supabaseAuth.signOut();
          return;
        }

        console.log('âœ… Driver record found:', driverRecord.id);

        const driverInfo = {
          uid: user.id,
          id: driverRecord.id,
          email: user.email,
          name: driverRecord.name,
          phone: driverRecord.phone,
          vehicleType: driverRecord.vehicle_type,
          vehicleNumber: driverRecord.vehicle_number,
          licenseNumber: driverRecord.license_number || 'N/A', // Handle missing field
          rating: driverRecord.rating || 5.0,
          totalRides: driverRecord.total_rides || 0,
          isOnline: false,
          currentLocation: null,
          available: driverRecord.available
        };

        localStorage.setItem('driverToken', authData.session.access_token);
        localStorage.setItem('driverData', JSON.stringify(driverInfo));

        console.log('âœ… Driver login completed successfully');
        if (onLogin) onLogin(driverInfo);
        navigate('/driver/dashboard');
      } else {
        // Register new driver
        console.log('ğŸ“ Starting driver registration process...');

        // Check if email already exists in drivers table
        const { data: existingDrivers } = await supabaseDB.drivers.getAll();
        const existingDriver = existingDrivers?.find(d => d.email === formData.email);

        if (existingDriver) {
          setError('A driver account with this email already exists. Please login instead.');
          return;
        }

        // Step 1: Create auth user
        const { data: authData, error: authError } = await supabaseAuth.signUp(
          formData.email, 
          formData.password,
          {
            full_name: formData.name,
            phone: formData.phone
          }
        );

        if (authError) {
          console.error('âŒ Auth registration failed:', authError);
          throw new Error(authError.message);
        }

        console.log('âœ… Auth user created:', authData.user?.id);

        // Step 2: Create driver record
        let driverRecord = null;
        let driverId = null;

        try {
          driverRecord = await createDriverRecord(authData.user);
          driverId = driverRecord.id;
          console.log('âœ… Driver profile created successfully:', driverId);
        } catch (dbError) {
          console.error('âŒ Database driver creation failed:', dbError.message);

          // Clean up auth user if driver creation fails
          if (authData.user) {
            console.log('ğŸ§¹ Cleaning up auth user due to driver creation failure...');
            try {
              await supabaseAuth.signOut();
            } catch (cleanupError) {
              console.warn('âš ï¸ Failed to clean up auth user:', cleanupError);
            }
          }

          // Show user-friendly error
          if (dbError.message.includes('permission') || dbError.message.includes('policy')) {
            setError('Registration temporarily unavailable. Please contact support or try again later.');
          } else {
            setError(dbError.message || 'Failed to create driver profile');
          }
          return;
        }

        const driverInfo = {
          uid: authData.user?.id,
          id: driverId,
          email: formData.email,
          name: formData.name,
          phone: formData.phone,
          vehicleType: formData.vehicleType,
          vehicleNumber: formData.vehicleNumber,
          licenseNumber: formData.licenseNumber || 'N/A', // Handle missing field
          rating: 5.0,
          totalRides: 0,
          isOnline: false,
          currentLocation: null,
          available: false
        };

        localStorage.setItem('driverToken', authData.session?.access_token || 'temp-token');
        localStorage.setItem('driverData', JSON.stringify(driverInfo));

        console.log('âœ… Driver registration completed successfully');
        if (onLogin) onLogin(driverInfo);
        navigate('/driver/dashboard');
      }
    } catch (error) {
      console.error('ğŸ’¥ Authentication error:', error);

      // Handle Supabase-specific error messages
      const message = error.message || 'Authentication failed';
      if (message.includes('Invalid login credentials') || message.includes('Invalid email or password')) {
        setError('Invalid email or password');
      } else if (message.includes('User already registered')) {
        setError('Email is already registered. Please login instead.');
      } else if (message.includes('Password should be at least')) {
        setError('Password must be at least 6 characters');
      } else if (message.includes('Invalid email')) {
        setError('Invalid email address');
      } else if (message.includes('Email rate limit exceeded')) {
        setError('Too many requests. Please try again in a few minutes.');
      } else {
        setError(message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="driver-login-container">
      <div className="driver-login-card">
        <div className="driver-login-header">
          <h2>ğŸš— Driver {isLogin ? 'Login' : 'Registration'}</h2>
          <p>Join BidCab as a professional driver</p>
        </div>

        {error && (
          <div className="error-message">
            <span className="error-icon">âš ï¸</span>
            <span>{error}</span>
          </div>
        )}

        <form onSubmit={handleLogin} className="driver-login-form">
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              required
            />
          </div>

          {!isLogin && (
            <>
              <div className="form-group">
                <label htmlFor="name">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Enter your full name"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  placeholder="Enter your phone number"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="vehicleType">Vehicle Type</label>
                <select
                  id="vehicleType"
                  name="vehicleType"
                  value={formData.vehicleType}
                  onChange={handleInputChange}
                  required
                >
                  <option value="sedan">Sedan</option>
                  <option value="hatchback">Hatchback</option>
                  <option value="suv">SUV</option>
                  <option value="mini">Mini</option>
                  <option value="luxury">Luxury</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="vehicleNumber">Vehicle Number</label>
                <input
                  type="text"
                  id="vehicleNumber"
                  name="vehicleNumber"
                  value={formData.vehicleNumber}
                  onChange={handleInputChange}
                  placeholder="Enter vehicle registration number"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="licenseNumber">Driving License Number</label>
                <input
                  type="text"
                  id="licenseNumber"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={handleInputChange}
                  placeholder="Enter driving license number"
                  required
                />
              </div>
            </>
          )}

          <button type="submit" className="driver-login-btn" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner">âŸ³</span>
                {isLogin ? 'Signing In...' : 'Registering...'}
              </>
            ) : (
              <>
                <span className="btn-icon">ğŸš—</span>
                {isLogin ? 'Login as Driver' : 'Register as Driver'}
              </>
            )}
          </button>
        </form>

        <div className="driver-login-footer">
          <p>
            {isLogin ? "New driver? " : "Already have an account? "}
            <button 
              type="button" 
              className="toggle-mode-btn" 
              onClick={() => {
                setIsLogin(!isLogin);
                setError('');
                setFormData({
                  email: '',
                  password: '',
                  name: '',
                  phone: '',
                  vehicleType: 'sedan',
                  vehicleNumber: '',
                  licenseNumber: ''
                });
              }}
            >
              {isLogin ? 'Register here' : 'Login here'}
            </button>
          </p>

          <p className="customer-link">
            Are you a customer? 
            <button 
              type="button" 
              className="customer-btn" 
              onClick={() => window.location.pathname = '/'}
            >
              Book a Ride
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default DriverLogin;

src/components/EnhancedOTPNotification.j.content:
import React, { useState, useEffect, useRef } from 'react';
import { supabaseDB } from '../utils/supabaseService';

const EnhancedOTPNotification = ({ driverData, onRideConfirmed }) => {
  const [confirmedRide, setConfirmedRide] = useState(null);
  const [otpInput, setOtpInput] = useState('');
  const [otpError, setOtpError] = useState('');
  const [startingRide, setStartingRide] = useState(false);
  const [notificationState, setNotificationState] = useState('hidden'); // hidden, showing, dismissed
  const [hasShownNotification, setHasShownNotification] = useState(new Set());

  // Use ref to track if we've already processed a specific ride
  const processedRides = useRef(new Set());
  const checkInterval = useRef(null);

  const driver = driverData || JSON.parse(localStorage.getItem('driverData') || '{}');

  // Enhanced notification check that prevents duplicates and reduces frequency
  useEffect(() => {
    const checkForAcceptedBid = async () => {
      // Don't check if we're already showing a notification or dismissed recently
      if (notificationState !== 'hidden') {
        return;
      }

      try {
        let rideToShow = null;

        // Try database first
        try {
          const { data: acceptedRides, error } = await supabaseDB.bookings.getAll();

          if (!error && acceptedRides && acceptedRides.length > 0) {
            // Find rides confirmed for this driver that we haven't processed yet
            rideToShow = acceptedRides.find(ride => 
              (ride.selected_driver_id === driver.id || 
               ride.selected_driver_id === driver.uid ||
               ride.driver_id === driver.id ||
               ride.driver_id === driver.uid) &&
              ride.status === 'confirmed' &&
              !ride.started_at && // Not started yet
              !processedRides.current.has(ride.id) && // Not already processed
              !hasShownNotification.has(ride.id) // Not already shown
            );
          }
        } catch (dbError) {
          console.log('Database check failed, trying localStorage...');
        }

        // Only check localStorage if database didn't find anything
        if (!rideToShow) {
          const acceptedBooking = JSON.parse(localStorage.getItem('acceptedBooking') || '{}');
          const confirmedBooking = JSON.parse(localStorage.getItem('confirmedBooking') || '{}');

          const bookingToCheck = confirmedBooking.id ? confirmedBooking : acceptedBooking;

          if (bookingToCheck.id && 
              bookingToCheck.selected_driver_id === (driver.id || driver.uid) && 
              bookingToCheck.status === 'confirmed' && 
              !processedRides.current.has(bookingToCheck.id) &&
              !hasShownNotification.has(bookingToCheck.id)) {
            rideToShow = bookingToCheck;
          }
        }

        // Only show notification if we found a new ride
        if (rideToShow) {
          console.log('ğŸ‰ New ride confirmation detected:', rideToShow.id);

          // Mark this ride as processed and shown permanently
          processedRides.current.add(rideToShow.id);
          setHasShownNotification(prev => new Set(prev).add(rideToShow.id));

          // Store in localStorage to persist across page reloads
          const shownRides = JSON.parse(localStorage.getItem('shownNotificationRides') || '[]');
          if (!shownRides.includes(rideToShow.id)) {
            shownRides.push(rideToShow.id);
            localStorage.setItem('shownNotificationRides', JSON.stringify(shownRides.slice(-50))); // Keep last 50
          }

          setConfirmedRide(rideToShow);
          setNotificationState('showing');

          // Play notification sound
          try {
            const audio = new Audio('/notification.mp3');
            audio.play().catch(() => console.log('Could not play notification sound'));
          } catch (e) {
            console.log('Notification sound not available');
          }
        }

      } catch (error) {
        console.error('Error checking for accepted bids:', error);
      }
    };

    // Load previously shown notifications to prevent re-showing
    const loadShownNotifications = () => {
      const shownRides = JSON.parse(localStorage.getItem('shownNotificationRides') || '[]');
      shownRides.forEach(rideId => {
        processedRides.current.add(rideId);
        setHasShownNotification(prev => new Set(prev).add(rideId));
      });
    };

    // Load shown notifications on mount
    loadShownNotifications();

    // Only start checking if we don't have an active notification
    if (notificationState === 'hidden') {
      // Initial check
      setTimeout(checkForAcceptedBid, 1000); // Delay initial check to avoid race conditions

      // Set up interval with reduced frequency
      checkInterval.current = setInterval(checkForAcceptedBid, 15000); // Check every 15 seconds
    }

    return () => {
      if (checkInterval.current) {
        clearInterval(checkInterval.current);
        checkInterval.current = null;
      }
    };
  }, [driver.id, driver.uid, notificationState]);

  // Handle OTP input changes
  const handleOtpChange = (value) => {
    // Only allow numeric input, max 4 digits
    const numericValue = value.replace(/\D/g, '').slice(0, 4);
    setOtpInput(numericValue);

    if (otpError) {
      setOtpError('');
    }
  };

  // Start the ride after OTP verification
  const startRide = async () => {
    if (!otpInput) {
      setOtpError('Please enter the customer\'s OTP');
      return;
    }

    if (otpInput.length !== 4) {
      setOtpError('OTP must be exactly 4 digits');
      return;
    }

    if (otpInput !== confirmedRide.otp) {
      setOtpError('Invalid OTP. Please ask the customer for the correct 4-digit code.');
      return;
    }

    setStartingRide(true);

    try {
      // Update ride status to in_progress
      let rideStarted = false;

      try {
        await supabaseDB.bookings.update(confirmedRide.id, {
          status: 'in_progress',
          started_at: new Date().toISOString()
        });
        rideStarted = true;
        console.log('âœ… Ride started in database');
      } catch (dbError) {
        // Fallback: Update localStorage
        const startedRide = {
          ...confirmedRide,
          status: 'in_progress',
          started_at: new Date().toISOString()
        };

        localStorage.setItem('activeRide', JSON.stringify(startedRide));
        localStorage.setItem(`ride_${confirmedRide.id}`, JSON.stringify(startedRide));

        // Remove from confirmed bookings
        localStorage.removeItem('confirmedBooking');
        localStorage.removeItem('acceptedBooking');

        rideStarted = true;
        console.log('âœ… Ride started in fallback mode');
      }

      if (rideStarted) {
        // Show success message
        const successMessage = `ğŸ‰ Ride Started Successfully!\n\nCustomer: ${confirmedRide.customer_name || confirmedRide.customerName}\nDestination: ${confirmedRide.drop_address || confirmedRide.drop}`;

        // Clear notification state
        setNotificationState('dismissed');
        setConfirmedRide(null);
        setOtpInput('');
        setOtpError('');

        // Notify parent component
        if (onRideConfirmed) {
          onRideConfirmed(confirmedRide);
        }

        // Show success alert and navigate
        alert(successMessage);

        // Use window.location to navigate (more reliable than navigate hook)
        window.location.href = '/driver/active-rides';
      }

    } catch (error) {
      console.error('Error starting ride:', error);
      setOtpError('Failed to start ride. Please try again.');
    } finally {
      setStartingRide(false);
    }
  };

  // Dismiss notification permanently
  const dismissNotification = () => {
    setNotificationState('dismissed');

    // Mark this ride as permanently dismissed
    if (confirmedRide) {
      processedRides.current.add(confirmedRide.id);
      setHasShownNotification(prev => new Set(prev).add(confirmedRide.id));

      // Store dismissal in localStorage to persist across page reloads
      const shownRides = JSON.parse(localStorage.getItem('shownNotificationRides') || '[]');
      if (!shownRides.includes(confirmedRide.id)) {
        shownRides.push(confirmedRide.id);
        localStorage.setItem('shownNotificationRides', JSON.stringify(shownRides.slice(-50)));
      }

      // Also remove from active booking data to prevent re-showing
      localStorage.removeItem('acceptedBooking');
      localStorage.removeItem('confirmedBooking');
      localStorage.removeItem(`booking_${confirmedRide.id}`);
    }

    setConfirmedRide(null);
    setOtpInput('');
    setOtpError('');

    // Reset to hidden state after a short delay
    setTimeout(() => {
      setNotificationState('hidden');
    }, 1000);
  };

  // Don't render if notification should be hidden
  if (notificationState !== 'showing' || !confirmedRide) {
    return null;
  }

  return (
    <div className="enhanced-notification-overlay">
      <div className="enhanced-notification-modal">
        {/* Header */}
        <div className="notification-header">
          <div className="success-icon">ğŸ‰</div>
          <h2>Ride Confirmed!</h2>
          <p>Customer accepted your bid</p>
          <div className="ride-id">Ride ID: {confirmedRide.id?.slice(-8) || 'N/A'}</div>
        </div>

        {/* Ride Details */}
        <div className="ride-details">
          <div className="customer-info">
            <h3>ğŸ‘¤ {confirmedRide.customer_name || confirmedRide.customerName || 'Customer'}</h3>
            {(confirmedRide.customer_phone || confirmedRide.customerPhone) && (
              <a href={`tel:${confirmedRide.customer_phone || confirmedRide.customerPhone}`} className="phone-link">
                ğŸ“ {confirmedRide.customer_phone || confirmedRide.customerPhone}
              </a>
            )}
          </div>

          <div className="route-info">
            <div className="route-item">
              <span className="icon">ğŸ“</span>
              <div>
                <div className="label">Pickup</div>
                <div className="address">{confirmedRide.pickup_address || confirmedRide.pickup || 'Pickup Location'}</div>
              </div>
            </div>
            <div className="route-arrow">â†“</div>
            <div className="route-item">
              <span className="icon">ğŸ</span>
              <div>
                <div className="label">Drop</div>
                <div className="address">{confirmedRide.drop_address || confirmedRide.drop || 'Drop Location'}</div>
              </div>
            </div>
          </div>

          <div className="fare-info">
            <span className="label">Fare:</span>
            <span className="amount">â‚¹{confirmedRide.final_fare || confirmedRide.price || confirmedRide.estimated_fare}</span>
          </div>
        </div>

        {/* OTP Section */}
        <div className="otp-section">
          <h3>ğŸ” Enter Customer's OTP</h3>
          <p className="otp-instruction">Ask the customer for their 4-digit OTP code to start the ride</p>

          <div className="otp-input-container">
            <input
              type="text"
              placeholder="0000"
              value={otpInput}
              onChange={(e) => handleOtpChange(e.target.value)}
              maxLength={4}
              className={`otp-input ${otpError ? 'error' : ''}`}
              autoFocus
              inputMode="numeric"
              pattern="[0-9]*"
            />
          </div>

          {otpError && (
            <div className="otp-error">
              âš ï¸ {otpError}
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="notification-actions">
          <button 
            className="start-ride-btn" 
            onClick={startRide}
            disabled={startingRide || otpInput.length !== 4}
          >
            {startingRide ? (
              <>âŸ³ Starting Ride...</>
            ) : (
              <>ğŸš— Start Ride</>
            )}
          </button>

          <button 
            className="dismiss-btn" 
            onClick={dismissNotification}
            disabled={startingRide}
          >
            âœ• Dismiss
          </button>
        </div>
      </div>

      {/* Enhanced Styles */}
      <style jsx>{`
