}

/* Dark Theme Support for New Elements */
body.dark .divider-line {
  background: #444;
}

body.dark .divider-text {
  color: #ccc;
}

body.dark .google-login-button {
  background: #2a2a2a;
  border-color: #444;
  color: #fff;
}

body.dark .google-login-button:hover {
  background: #333;
  border-color: #4285f4;
}

body.dark .demo-text {
  color: #a0aec0;
}

body.dark .divider-line {
  background: #718096;
}

body.dark .divider-text {
  color: #a0aec0;
}

body.dark .google-login-button {
  background: #4a5568;
  border-color: #718096;
