import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { database } from '../utils/database';
import { createApiUrl, API_ENDPOINTS } from '../config/api';

function History({ appState }) {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all'); // all, completed, cancelled

  useEffect(() => {
    loadBookingHistory();
  }, []);

  const loadBookingHistory = async () => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || localStorage.getItem('customerData') || '{}');
      const token = localStorage.getItem('customerToken') || localStorage.getItem('authToken');
      const userId = user?.id || user?.uid || 'demo-user-123';

      let allBookings = [];

      // Try to load from backend API first
      if (token) {
        try {
          const response = await fetch(createApiUrl(API_ENDPOINTS.CUSTOMER_HISTORY), {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });

          if (response.ok) {
            const data = await response.json();
            if (data.success && data.history) {
              allBookings = data.history;
              console.log('âœ… Found backend API booking history:', allBookings.length);

              // Update stats from API data
              setStats({
                totalBookings: data.totalBookings || allBookings.length,
                completedRides: data.completedRides || allBookings.filter(b => b.status === 'completed').length,
                cancelledRides: allBookings.filter(b => b.status === 'cancelled').length,
                totalSpent: data.totalSpent || 0,
                averageRating: 4.8
              });
            }
          } else {
            console.log('âš ï¸ API unavailable, falling back to local storage...');
          }
        } catch (apiError) {
          console.log('âš ï¸ API error:', apiError.message, ', checking localStorage...');
        }
      }

      // Fallback: Try to load from database
      if (allBookings.length === 0) {
        try {
          const userBookings = await database.getUserBookingHistory(userId);
          if (userBookings && userBookings.length > 0) {
            allBookings = userBookings;
            console.log('âœ… Found database booking history:', allBookings.length);
          }
        } catch (dbError) {
          console.log('âš ï¸ Database unavailable, checking localStorage...');
        }
      }

      // Fallback: Load from localStorage
      if (allBookings.length === 0) {
        const localHistory = JSON.parse(localStorage.getItem('customerRideHistory') || '[]');
        const confirmedBookings = JSON.parse(localStorage.getItem('confirmedBooking') || '{}');

        // Add confirmed booking if it exists
        if (confirmedBookings.id) {
          localHistory.unshift(confirmedBookings);
        }

        // Remove duplicates based on ID
        const uniqueBookings = localHistory.filter((booking, index, self) => 
          index === self.findIndex(b => b.id === booking.id)
        );

        allBookings = uniqueBookings;
        console.log('ğŸ“ Found localStorage booking history:', allBookings.length);
      }

      // Sort by date (newest first)
      allBookings.sort((a, b) => 
        new Date(b.completed_at || b.timestamp || b.created_at) - 
        new Date(a.completed_at || a.timestamp || a.created_at)
      );

      setBookings(allBookings);

      // Calculate stats from actual booking data
      const completedRides = allBookings.filter(b => b.status === 'completed');
      const totalSpent = allBookings.reduce((sum, b) => 
        sum + parseFloat(b.final_fare || b.estimated_fare || b.price || 0), 0
      );

      setStats({
        totalBookings: allBookings.length,
        completedRides: completedRides.length,
        cancelledRides: allBookings.filter(b => b.status === 'cancelled').length,
        totalSpent: totalSpent,
        averageRating: 4.8 // Can be calculated from actual ratings later
      });

    } catch (error) {
      console.error('Error loading booking history:', error);
      setBookings([]);
      setStats({});
    } finally {
      setLoading(false);
    }
  };

  const filteredBookings = bookings.filter(booking => {
    if (filter === 'all') return true;
    return booking.status === filter;
  });

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusIcon = (status) => {
    switch(status) {
      case 'completed': return 'âœ…';
      case 'confirmed': return 'ğŸ”„';
      case 'cancelled': return 'âŒ';
      default: return 'ğŸ“‹';
    }
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'completed': return '#10b981';
      case 'confirmed': return '#3b82f6';
      case 'cancelled': return '#ef4444';
      default: return '#6b7280';
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading your booking history...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="history-page">
        {/* Header */}
        <div className="history-header">
          <h1>ğŸš— My Rides</h1>
          <p>Track all your cab bookings and ride history</p>
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">ğŸ¯</div>
            <div className="stat-info">
              <div className="stat-value">{stats.totalBookings || 0}</div>
              <div className="stat-label">Total Rides</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">âœ…</div>
            <div className="stat-info">
              <div className="stat-value">{stats.completedRides || 0}</div>
              <div className="stat-label">Completed</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">ğŸ’°</div>
            <div className="stat-info">
              <div className="stat-value">â‚¹{stats.totalSpent || 0}</div>
              <div className="stat-label">Total Spent</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">â­</div>
            <div className="stat-info">
              <div className="stat-value">{(stats.averageRating || 5).toFixed(1)}</div>
              <div className="stat-label">Avg Rating</div>
            </div>
          </div>
        </div>

        {/* Filter Tabs */}
        <div className="filter-tabs">
          <button 
            className={`filter-tab ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            All ({bookings.length})
          </button>
          <button 
            className={`filter-tab ${filter === 'completed' ? 'active' : ''}`}
            onClick={() => setFilter('completed')}
          >
            Completed ({bookings.filter(b => b.status === 'completed').length})
          </button>
          <button 
            className={`filter-tab ${filter === 'confirmed' ? 'active' : ''}`}
            onClick={() => setFilter('confirmed')}
          >
            Active ({bookings.filter(b => b.status === 'confirmed').length})
          </button>
          <button 
            className={`filter-tab ${filter === 'cancelled' ? 'active' : ''}`}
            onClick={() => setFilter('cancelled')}
          >
            Cancelled ({bookings.filter(b => b.status === 'cancelled').length})
          </button>
        </div>

        {/* Bookings List */}
        <div className="bookings-list">
          {filteredBookings.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">ğŸ“­</div>
              <h3>No rides found</h3>
              <p>
                {filter === 'all' 
                  ? "You haven't booked any rides yet. Start your first journey!"
                  : `No ${filter} rides found.`
                }
              </p>
              {filter === 'all' && (
                <button className="primary-btn" onClick={() => navigate('/')}>
                  Book Your First Ride
                </button>
              )}
            </div>
          ) : (
            filteredBookings.map((booking) => (
              <div key={booking.id} className="booking-card">
                <div className="booking-header">
                  <div className="booking-status">
                    <span className="status-icon">{getStatusIcon(booking.status)}</span>
                    <span 
                      className="status-text" 
                      style={{ color: getStatusColor(booking.status) }}
                    >
                      {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                    </span>
                  </div>
                  <div className="booking-date">{formatDate(booking.timestamp || booking.created_at)}</div>
                </div>

                <div className="booking-route">
                  <div className="route-point">
                    <div className="route-icon pickup">ğŸ“</div>
                  <div className="route-text">
                    <div className="route-label">From</div>
                    <div className="route-address">{booking.pickup_address || booking.pickup || 'Unknown Location'}</div>
                  </div>
                </div>
                <div className="route-arrow">â†’</div>
                <div className="route-point">
                  <div className="route-icon drop">ğŸ</div>
                  <div className="route-text">
                    <div className="route-label">To</div>
                    <div className="route-address">{booking.drop_address || booking.drop || 'Unknown Destination'}</div>
                  </div>
                  </div>
                </div>

              <div className="booking-details">
                <div className="detail-item">
                  <span className="detail-icon">ğŸš—</span>
                  <span className="detail-text">{booking.driver_name || booking.driverName || 'Driver'}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">ğŸš™</span>
                  <span className="detail-text">{booking.vehicle_model || booking.car || 'Vehicle'}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">ğŸ“</span>
                  <span className="detail-text">{booking.distance || 0} km</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">ğŸ’°</span>
                  <span className="detail-text">â‚¹{booking.estimated_fare || booking.price || 0}</span>
                </div>
              </div>

                {booking.bookingId && (
                  <div className="booking-id">
                    <span>Booking ID: </span>
                    <span className="id-value">{booking.bookingId}</span>
                  </div>
                )}

                <div className="booking-actions">
                  {booking.status === 'confirmed' && (
                    <>
                      <button className="action-btn track-btn">
                        ğŸ“ Track Ride
                      </button>
                      <button className="action-btn call-btn">
                        ğŸ“ Call Driver
                      </button>
                    </>
                  )}
                  {booking.status === 'completed' && (
                    <button className="action-btn rate-btn">
                      â­ Rate Ride
                    </button>
                  )}
                  <button className="action-btn details-btn">
                    ğŸ“‹ View Details
                  </button>
                </div>
              </div>
            ))
          )}
        </div>

        {filteredBookings.length > 0 && (
          <div className="pagination-info">
            Showing {filteredBookings.length} of {bookings.length} rides
          </div>
        )}
      </div>
    </div>
  );
}

export default History;

src/pages/Home.js.content:
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import PreciseLocationMap from '../components/PreciseLocationMap';
import { supabaseDB } from '../utils/supabaseService';
import { createApiUrl, API_ENDPOINTS } from '../config/api';

const Home = ({ appState }) => {
  const navigate = useNavigate();
  const {
    pickup, setPickup, drop, setDrop, suggestedPrice, setSuggestedPrice,
    useSuggestedPrice, setUseSuggestedPrice, locationLoading, setLocationLoading,
    locationError, setLocationError, ReverseGeocode
  } = appState;

  // The PreciseLocationMap component handles location detection and map centering
  // We keep the detectLocation function for the manual location button

  const geocodeAddress = async (address, setLocation) => {
    try {
      const res = await axios.get(`https://nominatim.openstreetmap.org/search`, {
        params: { q: address, format: 'json', limit: 1 },
      });
      if (res.data[0]) {
        const { lat, lon } = res.data[0];
        setLocation({ address, coords: [parseFloat(lat), parseFloat(lon)] });
      }
    } catch (err) {
      console.warn('Geocoding failed:', err);
    }
  };

  const detectLocation = () => {
    if (!navigator.geolocation) {
      setLocationError('Geolocation is not supported by this browser.');
      return;
    }

    setLocationLoading(true);
    setLocationError('');

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        try {
          const { latitude, longitude } = position.coords;
          const coords = [latitude, longitude];
          const address = await ReverseGeocode(latitude, longitude);

          // Update pickup location (PreciseLocationMap will handle map centering)
          setPickup({ coords, address });

          setLocationLoading(false);
        } catch (error) {
          setLocationError('Failed to get address for your location.');
          setLocationLoading(false);
        }
      },
      (error) => {
        let errorMessage = 'Unable to retrieve your location.';
        switch (error.code) {
          case error.PERMISSION_DENIED:
            errorMessage = 'Location access denied. Please enable location permission.';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = 'Location information is unavailable.';
            break;
          case error.TIMEOUT:
            errorMessage = 'Location request timed out.';
            break;
          default:
            errorMessage = 'An unknown error occurred while retrieving location.';
            break;
        }
        setLocationError(errorMessage);
        setLocationLoading(false);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 60000
      }
    );
  };

  const startBidding = async () => {
    if (!pickup.address || !drop.address) {
      alert('Please set both pickup and drop locations before starting bidding.');
      return;
    }

    try {
      // Calculate estimated distance and fare
      const calculateDistance = (pickup, drop) => {
        if (!pickup.coords || !drop.coords) return 5; // Default distance
        const R = 6371; // Earth's radius in kilometers
        const dLat = (drop.coords[0] - pickup.coords[0]) * Math.PI / 180;
        const dLng = (drop.coords[1] - pickup.coords[1]) * Math.PI / 180;
        const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
                  Math.cos(pickup.coords[0] * Math.PI / 180) * Math.cos(drop.coords[0] * Math.PI / 180) *
                  Math.sin(dLng/2) * Math.sin(dLng/2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
        return R * c;
      };

      const distance = calculateDistance(pickup, drop);
      const estimatedFare = Math.round(distance * 15 + 50); // Base fare calculation

      // Get user data from localStorage if available
      const userData = JSON.parse(localStorage.getItem('customerData') || '{}');
      const customerToken = localStorage.getItem('customerToken');

      // Create ride request data
      const rideRequestData = {
        customer_name: userData.full_name || userData.name || 'Customer',
        customer_phone: userData.phone || '+91 0000000000',
        pickup_address: pickup.address,
        drop_address: drop.address,
        pickup_location: pickup.coords ? { lat: pickup.coords[0], lng: pickup.coords[1] } : null,
        drop_location: drop.coords ? { lat: drop.coords[0], lng: drop.coords[1] } : null,
        distance: distance,
        estimated_fare: useSuggestedPrice && suggestedPrice ? parseFloat(suggestedPrice) : estimatedFare,
        status: 'pending',
        payment_method: 'cash'
      };

      let rideRequestId = null;

      // Try Supabase database first
      try {
        const { data, error } = await supabaseDB.bookings.add(rideRequestData);

        if (error) {
          console.warn('Supabase booking failed:', error);
          throw new Error('Supabase booking failed');
        }

        rideRequestId = data[0].id;
        console.log('âœ… Ride request created in Supabase:', rideRequestId);
      } catch (supabaseError) {
        console.log('Supabase unavailable, trying backend API...');

        // Fallback to backend API if available
        try {
          // If user is authenticated, use the bid endpoint
          if (customerToken) {
            const response = await axios.post(createApiUrl(API_ENDPOINTS.BID), {
              pickup: pickup.address,
              drop: drop.address,
              ...rideRequestData
            }, {
              headers: {
                'Authorization': `Bearer ${customerToken}`,
                'Content-Type': 'application/json'
              }
            });

            if (response.data) {
              console.log('âœ… Ride request processed via backend API');
              // Use a mock ID for now
              rideRequestId = 'backend_' + Date.now();
            }
          } else {
            // No authentication, proceed with demo data
            console.log('ğŸ“ No authentication - proceeding with demo mode');
            rideRequestId = 'demo_' + Date.now();
          }
        } catch (apiError) {
          console.log('Backend API unavailable, proceeding with demo mode...');
          rideRequestId = 'demo_' + Date.now();
        }
      }

      // Store the ride request ID for later use
      localStorage.setItem('currentRideRequestId', rideRequestId);
      localStorage.setItem('currentRideRequest', JSON.stringify(rideRequestData));

      console.log('ğŸš— Ride request created with ID:', rideRequestId);

      // Navigate to bidding page
      navigate('/bids');
    } catch (error) {
      console.error('Error starting bidding:', error);
      alert(`Failed to create ride request: ${error.message}. Please check your connection and try again.`);
    }
  };

  return (
    <div className="home-page">
      {/* Location error display */}
      {locationError && (
        <div className="location-error-display">
          <span className="error-icon">âš ï¸</span>
          <span className="error-text">{locationError}</span>
        </div>
      )}

      <div className="pickup-input-container">
        <label>Pickup Location</label>
        <div className="input-with-icon">
          <input
            type="text"
            value={pickup.address}
            onChange={e => setPickup({ ...pickup, address: e.target.value })}
            onBlur={() => geocodeAddress(pickup.address, setPickup)}
            placeholder="Enter pickup location or click location icon"
            className="location-input"
          />
          <button
            type="button"
            className="location-detect-icon"
            onClick={detectLocation}
            disabled={locationLoading}
            title="Detect my current location"
          >
            {locationLoading ? (
              <span className="detect-icon spinning">âŸ³</span>
            ) : (
              <span className="detect-icon">â¤</span>
            )}
          </button>
        </div>
      </div>

      <div className="drop-input-container">
        <label>Drop Location</label>
        <div className="input-with-icon">
          <input
            value={drop.address}
            onChange={(e) => setDrop({ ...drop, address: e.target.value })}
            onBlur={() => geocodeAddress(drop.address, setDrop)}
            placeholder="Type address or click on map"
            className="location-input"
          />
        </div>
      </div>

      <div style={{ margin: '10px 0' }}>
        <label>
          <input
            type="checkbox"
            checked={useSuggestedPrice}
            onChange={e => setUseSuggestedPrice(e.target.checked)}
            style={{ marginRight: 6 }}
          />
          Suggest a Price
        </label>
        {useSuggestedPrice && (
          <input
            type="number"
            min="0"
            value={suggestedPrice}
            onChange={e => setSuggestedPrice(e.target.value)}
            placeholder="Enter suggested price in â‚¹"
            style={{ width: 120, marginLeft: 10 }}
          />
        )}
      </div>

      {/* Use PreciseLocationMap component for exact location detection */}
      <PreciseLocationMap
        pickup={pickup}
        setPickup={setPickup}
        drop={drop}
        setDrop={setDrop}
        onLocationDetected={(locationData) => {
          console.log('Precise location detected:', locationData);
          // The component already handles setting pickup location
          // This callback can be used for additional actions if needed
        }}
        onLocationError={(error) => {
          setLocationError(error);
          console.error('Location error:', error);
        }}
        ReverseGeocode={ReverseGeocode}
        className="home-map"
        height="400px"
      />

      <div className="start-bidding-section">
        <button onClick={startBidding} className="start-bidding-btn">
          <span className="btn-icon">ğŸš•</span>
          <span className="btn-text">Find My Bid</span>
          <span className="btn-subtitle">Get competitive offers</span>
        </button>
      </div>
    </div>
  );
};

export default Home;

