import { supabase } from './supabase.js';

// Check if supabase is properly initialized
const isSupabaseReady = () => {
  return supabase && typeof supabase.from === 'function';
};

// Authentication Functions
export const supabaseAuth = {
  // Sign up new user
  signUp: async (email, password, userData = {}) => {
    try {
      if (!isSupabaseReady()) {
        throw new Error('Supabase not initialized');
      }
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: userData
        }
      });
      return { data, error };
    } catch (error) {
      console.error('Sign up error:', error);
      return { data: null, error };
    }
  },

  // Sign in user
  signIn: async (email, password) => {
    try {
      if (!isSupabaseReady()) {
        throw new Error('Supabase not initialized');
      }
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password
      });
      return { data, error };
    } catch (error) {
      console.error('Sign in error:', error);
      return { data: null, error };
    }
  },

  // Sign out user
  signOut: async () => {
    try {
      if (!isSupabaseReady()) {
        throw new Error('Supabase not initialized');
      }
      const { error } = await supabase.auth.signOut();
      return { error };
    } catch (error) {
      console.error('Sign out error:', error);
      return { error };
    }
  },

  // Get current user
  getCurrentUser: () => {
    if (!isSupabaseReady()) {
      return { data: null, error: new Error('Supabase not initialized') };
    }
    return supabase.auth.getUser();
  },

  // Listen to auth state changes
  onAuthStateChange: (callback) => {
    if (!isSupabaseReady()) {
      return () => {}; // Return empty unsubscribe function
    }
    return supabase.auth.onAuthStateChange(callback);
  }
};

// Database Functions
export const supabaseDB = {
  // Drivers operations
  drivers: {
    // Get all drivers
    getAll: async () => {
      try {
        if (!isSupabaseReady()) {
          return { data: [], error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('drivers')
          .select('*');
        return { data: data || [], error };
      } catch (error) {
        console.error('Get drivers error:', error);
        return { data: [], error };
      }
    },

    // Get driver by email
    getByEmail: async (email) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('drivers')
          .select('*')
          .eq('email', email)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get driver by email error:', error);
        return { data: null, error };
      }
    },

    // Get driver by ID
    getById: async (id) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('drivers')
          .select('*')
          .eq('id', id)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get driver by ID error:', error);
        return { data: null, error };
      }
    },

    // Get driver by user_id (auth user ID)
    getByUserId: async (userId) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('drivers')
          .select('*')
          .eq('user_id', userId)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get driver by user ID error:', error);
        return { data: null, error };
      }
    },

    // Get available drivers
    getAvailable: async () => {
      try {
        const { data, error } = await supabase
          .from('drivers')
          .select('*')
          .eq('available', true);
        return { data, error };
      } catch (error) {
        console.error('Get available drivers error:', error);
        return { data: null, error };
      }
    },

    // Add new driver
    add: async (driverData) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('drivers')
          .insert([driverData])
          .select();
        return { data, error };
      } catch (error) {
        console.error('Add driver error:', error);
        return { data: null, error };
      }
    },

    // Update driver
    update: async (id, updateData) => {
      try {
        const { data, error } = await supabase
          .from('drivers')
          .update(updateData)
          .eq('id', id)
          .select();
        return { data, error };
      } catch (error) {
        console.error('Update driver error:', error);
        return { data: null, error };
      }
    },

    // Delete driver
    delete: async (id) => {
      try {
        const { data, error } = await supabase
          .from('drivers')
          .delete()
          .eq('id', id);
        return { data, error };
      } catch (error) {
        console.error('Delete driver error:', error);
        return { data: null, error };
      }
    }
  },

  // Bookings operations
  bookings: {
    // Get all bookings
    getAll: async () => {
      try {
        if (!isSupabaseReady()) {
          return { data: [], error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('bookings')
          .select('*')
          .order('created_at', { ascending: false });
        return { data: data || [], error };
      } catch (error) {
        console.error('Get bookings error:', error);
        return { data: [], error };
      }
    },

    // Get booking by ID
    getById: async (id) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('bookings')
          .select('*')
          .eq('id', id)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get booking by ID error:', error);
        return { data: null, error };
      }
    },

    // Get bookings by status
    getByStatus: async (status) => {
      try {
        if (!isSupabaseReady()) {
          return { data: [], error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('bookings')
          .select('*')
          .eq('status', status)
          .order('created_at', { ascending: false });
        return { data: data || [], error };
      } catch (error) {
        console.error('Get bookings by status error:', error);
        return { data: [], error };
      }
    },

    // Add new booking
    add: async (bookingData) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('bookings')
          .insert([bookingData])
          .select();
        return { data, error };
      } catch (error) {
        console.error('Add booking error:', error);
        return { data: null, error };
      }
    },

    // Update booking
    update: async (id, updateData) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('bookings')
          .update(updateData)
          .eq('id', id)
          .select();
        return { data, error };
      } catch (error) {
        console.error('Update booking error:', error);
        return { data: null, error };
      }
    },

    // Delete booking
    delete: async (id) => {
      try {
        const { data, error } = await supabase
          .from('bookings')
          .delete()
          .eq('id', id);
        return { data, error };
      } catch (error) {
        console.error('Delete booking error:', error);
        return { data: null, error };
      }
    }
  },

  // Bids operations
  bids: {
    // Get all bids
    getAll: async () => {
      try {
        const { data, error } = await supabase
          .from('bids')
          .select('*')
          .order('created_at', { ascending: false });
        return { data, error };
      } catch (error) {
        console.error('Get bids error:', error);
        return { data: null, error };
      }
    },

    // Get bids for a specific booking
    getByBooking: async (bookingId) => {
      try {
        const { data, error } = await supabase
          .from('bids')
          .select('*')
          .eq('booking_id', bookingId)
          .order('amount', { ascending: true });
        return { data, error };
      } catch (error) {
        console.error('Get bids by booking error:', error);
        return { data: null, error };
      }
    },

    // Add new bid
    add: async (bidData) => {
      try {
        const { data, error } = await supabase
          .from('bids')
          .insert([{
            ...bidData,
            created_at: new Date().toISOString()
          }])
          .select();
        return { data, error };
      } catch (error) {
        console.error('Add bid error:', error);
        return { data: null, error };
      }
    },

    // Update bid
    update: async (id, updateData) => {
      try {
        const { data, error } = await supabase
          .from('bids')
          .update(updateData)
          .eq('id', id)
          .select();
        return { data, error };
      } catch (error) {
        console.error('Update bid error:', error);
        return { data: null, error };
      }
    },

    // Delete bid
    delete: async (id) => {
      try {
        const { data, error } = await supabase
          .from('bids')
          .delete()
          .eq('id', id);
        return { data, error };
      } catch (error) {
        console.error('Delete bid error:', error);
        return { data: null, error };
      }
    }
  },

  // RPC functions for enhanced ride system
  rpc: async (functionName, parameters = {}) => {
    try {
      if (!isSupabaseReady()) {
        return { data: null, error: new Error('Supabase not initialized') };
      }
      const { data, error } = await supabase.rpc(functionName, parameters);
      return { data, error };
    } catch (error) {
      console.error(`RPC ${functionName} error:`, error);
      return { data: null, error };
    }
  },

  // Active rides operations
  activeRides: {
    // Get active ride for driver
    getByDriverId: async (driverId) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('active_rides')
          .select(`
            *,
            ride_history:ride_history_id(*),
            booking:booking_id(*)
          `)
          .eq('driver_id', driverId)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get active ride error:', error);
        return { data: null, error };
      }
    },

    // Get active ride by user ID (auth user ID)
    getByDriverUserId: async (driverUserId) => {
      try {
        if (!isSupabaseReady()) {
          return { data: null, error: new Error('Supabase not initialized') };
        }
        const { data, error } = await supabase
          .from('active_rides')
          .select(`
            *,
            ride_history:ride_history_id(*),
            booking:booking_id(*)
          `)
          .eq('driver_user_id', driverUserId)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get active ride by user ID error:', error);
        return { data: null, error };
      }
    }
  },

  // Bid timers operations
  bidTimers: {
    // Get timer for booking
    getByBookingId: async (bookingId) => {
      try {
        const { data, error } = await supabase
          .from('bid_timers')
          .select('*')
          .eq('booking_id', bookingId)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get bid timer error:', error);
        return { data: null, error };
      }
    },

    // Create new timer
    create: async (bookingId, duration = 300) => {
      try {
        const expiresAt = new Date(Date.now() + duration * 1000);
        const { data, error } = await supabase
          .from('bid_timers')
          .insert([{
            booking_id: bookingId,
            timer_duration: duration,
            expires_at: expiresAt.toISOString(),
            remaining_seconds: duration
          }])
          .select()
          .single();
        return { data, error };
      } catch (error) {
        console.error('Create bid timer error:', error);
        return { data: null, error };
      }
    }
  },

  // Ride history operations
  rideHistory: {
    // Get ride history for user
    getByUserId: async (userId) => {
      try {
        const { data, error } = await supabase
          .from('ride_history')
          .select('*')
          .eq('user_id', userId)
          .order('completed_at', { ascending: false });
        return { data: error ? [] : data, error };
      } catch (error) {
        console.error('Get user ride history error:', error);
        return { data: [], error };
      }
    },

    // Get ride history for driver
    getByDriverUserId: async (driverUserId) => {
      try {
        const { data, error } = await supabase
          .from('ride_history')
          .select('*')
          .eq('driver_user_id', driverUserId)
          .order('completed_at', { ascending: false });
        return { data: error ? [] : data, error };
      } catch (error) {
        console.error('Get driver ride history error:', error);
        return { data: [], error };
      }
    }
  },

  // Driver earnings operations
  driverEarnings: {
    // Get earnings for driver
    getByDriverId: async (driverId) => {
      try {
        const { data, error } = await supabase
          .from('driver_earnings')
          .select('*')
          .eq('driver_id', driverId)
          .single();
        return { data, error };
      } catch (error) {
        console.error('Get driver earnings error:', error);
        return { data: null, error };
      }
    }
  },

  // Real-time subscriptions
  realtime: {
    // Subscribe to bookings changes
    subscribeToBookings: (callback) => {
      return supabase
        .channel('bookings')
        .on('postgres_changes', 
          { event: '*', schema: 'public', table: 'bookings' }, 
          callback
        )
        .subscribe();
    },

    // Subscribe to bids changes
    subscribeToBids: (callback) => {
      return supabase
        .channel('bids')
        .on('postgres_changes', 
          { event: '*', schema: 'public', table: 'bids' }, 
          callback
        )
        .subscribe();
    },

    // Subscribe to drivers changes
    subscribeToDrivers: (callback) => {
      return supabase
        .channel('drivers')
        .on('postgres_changes', 
          { event: '*', schema: 'public', table: 'drivers' }, 
          callback
        )
        .subscribe();
    },

    // Subscribe to active rides changes
    subscribeToActiveRides: (callback) => {
      return supabase
        .channel('active_rides')
        .on('postgres_changes', 
          { event: '*', schema: 'public', table: 'active_rides' }, 
          callback
        )
        .subscribe();
    },

    // Subscribe to bid timers changes
    subscribeToBidTimers: (callback) => {
      return supabase
        .channel('bid_timers')
        .on('postgres_changes', 
          { event: '*', schema: 'public', table: 'bid_timers' }, 
          callback
        )
        .subscribe();
    },

    // Subscribe to ride history changes
    subscribeToRideHistory: (callback) => {
      return supabase
        .channel('ride_history')
        .on('postgres_changes', 
          { event: '*', schema: 'public', table: 'ride_history' }, 
          callback
        )
        .subscribe();
    },

    // Unsubscribe from a channel
    unsubscribe: (subscription) => {
      if (subscription) {
        supabase.removeChannel(subscription);
      }
    }
  }
};

// Utility function to handle errors consistently
export const handleSupabaseError = (error) => {
  if (error) {
    console.error('Supabase error:', error);
    return {
      message: error.message || 'An error occurred',
      details: error.details || null,
      hint: error.hint || null
    };
  }
  return null;
};

export default { supabaseAuth, supabaseDB, handleSupabaseError };

driver-app/src/components/Login.css.content:
/* Login Container */
.login-container {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  padding: 20px;
  font-family: 'Inter', sans-serif;
}

body.dark .login-container {
  background: linear-gradient(135deg, #d63031 0%, #e17055 100%);
}

.login-card {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 40px;
  width: 100%;
  max-width: 500px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

body.dark .login-card {
  background: rgba(42, 42, 42, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

/* Login Header */
.login-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 30px;
}

.brand-section {
  text-align: left;
}

.brand-icon {
  font-size: 3rem;
  margin-bottom: 10px;
}

.brand-section h1 {
  margin: 0 0 8px 0;
  font-size: 2rem;
  font-weight: 700;
  color: #1f2937;
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

body.dark .brand-section h1 {
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.brand-section p {
  margin: 0;
  color: #6b7280;
  font-size: 1rem;
  font-weight: 400;
}

body.dark .brand-section p {
  color: #9ca3af;
}

.theme-toggle {
  background: rgba(255, 107, 53, 0.1);
  border: 2px solid rgba(255, 107, 53, 0.2);
  border-radius: 50%;
  width: 50px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.theme-toggle:hover {
  background: rgba(255, 107, 53, 0.2);
  transform: scale(1.1);
}

body.dark .theme-toggle {
  background: rgba(255, 107, 53, 0.15);
  border-color: rgba(255, 107, 53, 0.3);
}

/* Login Tabs */
.login-tabs {
  display: flex;
  margin-bottom: 30px;
  background: #f3f4f6;
  border-radius: 12px;
  padding: 4px;
}

body.dark .login-tabs {
  background: #4b5563;
}

.tab {
  flex: 1;
  padding: 12px 20px;
  background: transparent;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  color: #6b7280;
  cursor: pointer;
  transition: all 0.3s ease;
}

.tab.active {
  background: white;
  color: #ff6b35;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

body.dark .tab {
  color: #9ca3af;
}

body.dark .tab.active {
  background: #1f2937;
  color: #ff6b35;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
}

/* Login Form */
.login-form {
  margin-bottom: 30px;
}

.form-group {
  margin-bottom: 20px;
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 15px;
}

@media (max-width: 480px) {
  .form-row {
    grid-template-columns: 1fr;
    gap: 20px;
  }
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  font-weight: 600;
  color: #374151;
  font-size: 0.9rem;
}

body.dark .form-group label {
  color: #d1d5db;
}

.form-group input,
.form-group select {
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e5e7eb;
  border-radius: 10px;
  font-size: 1rem;
  transition: all 0.3s ease;
  background: white;
  box-sizing: border-box;
}

.form-group input:focus,
.form-group select:focus {
  outline: none;
  border-color: #ff6b35;
  box-shadow: 0 0 0 3px rgba(255, 107, 53, 0.1);
}

body.dark .form-group input,
body.dark .form-group select {
  background: #374151;
  border-color: #4b5563;
  color: #f9fafb;
}

body.dark .form-group input:focus,
body.dark .form-group select:focus {
  border-color: #ff6b35;
  box-shadow: 0 0 0 3px rgba(255, 107, 53, 0.15);
}

/* Error Message */
.error-message {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  background: #fef2f2;
  border: 1px solid #fecaca;
  border-radius: 8px;
  color: #dc2626;
  font-size: 0.9rem;
  margin-bottom: 20px;
}

body.dark .error-message {
  background: #2d1b1b;
  border-color: #8b4949;
  color: #fca5a5;
}

.error-icon {
  font-size: 1.1rem;
}

/* Login Button */
.login-btn {
  width: 100%;
  padding: 15px 20px;
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  color: white;
  border: none;
  border-radius: 12px;
  font-size: 1.1rem;
  font-weight: 700;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  box-shadow: 0 4px 15px rgba(255, 107, 53, 0.3);
}

.login-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(255, 107, 53, 0.4);
}

.login-btn:disabled {
  opacity: 0.7;
  cursor: not-allowed;
  transform: none;
}

.loading-spinner {
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.btn-arrow {
  font-size: 1.2rem;
  transition: transform 0.3s ease;
}

.login-btn:hover .btn-arrow {
  transform: translateX(4px);
}

/* Demo Section */
.demo-section {
  margin-bottom: 20px;
}

.divider {
  position: relative;
  text-align: center;
  margin: 20px 0;
}

.divider::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: 1px;
  background: #e5e7eb;
}

body.dark .divider::before {
  background: #4b5563;
}

.divider span {
  background: white;
  padding: 0 15px;
  color: #6b7280;
  font-size: 0.9rem;
  font-weight: 500;
}

body.dark .divider span {
  background: #2a2a2a;
  color: #9ca3af;
}

.demo-btn {
  width: 100%;
  padding: 12px 20px;
  background: rgba(255, 107, 53, 0.1);
  color: #ff6b35;
  border: 2px solid rgba(255, 107, 53, 0.2);
  border-radius: 10px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
}

.demo-btn:hover {
  background: rgba(255, 107, 53, 0.15);
  transform: translateY(-1px);
}

body.dark .demo-btn {
  background: rgba(255, 107, 53, 0.15);
