import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Login = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    licenseNumber: '',
    vehicleModel: '',
    vehicleColor: '',
    vehicleType: 'sedan',
    plateNumber: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'light');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const endpoint = isLogin ? '/api/driver/login' : '/api/driver/register';
      const response = await axios.post(`http://localhost:3001${endpoint}`, formData);

      if (response.data.success) {
        localStorage.setItem('driverToken', response.data.token);
        localStorage.setItem('driver', JSON.stringify(response.data.driver));
        onLogin(response.data.driver);
      } else {
        setError(response.data.message || 'Authentication failed');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Network error occurred');
      console.error('Auth error:', err);
    } finally {
      setLoading(false);
    }
  };

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    document.body.className = newTheme;
    localStorage.setItem('theme', newTheme);
  };

  // Demo login with sample data
  const handleDemoLogin = () => {
    const demoDriver = {
      id: 'demo_driver_1',
      name: 'John Driver',
      email: 'john@example.com',
      phone: '+1234567890',
      licenseNumber: 'DL123456789',
      vehicleModel: 'Toyota Camry',
      vehicleColor: 'White',
      vehicleType: 'sedan',
      plateNumber: 'ABC-123',
      rating: 4.8,
      totalRides: 245,
      isOnline: false,
      avatar: 'JD'
    };

    localStorage.setItem('driverToken', 'demo_token');
    localStorage.setItem('driver', JSON.stringify(demoDriver));
    onLogin(demoDriver);
  };

  React.useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="brand-section">
            <div className="brand-icon">ğŸš—</div>
            <h1>BidCab Driver</h1>
            <p>Start earning with every ride</p>
          </div>

          <button 
            className="theme-toggle"
            onClick={toggleTheme}
            title={`Switch to ${theme === 'light' ? 'dark' : 'light'} mode`}
          >
            {theme === 'light' ? 'ğŸŒ™' : 'â˜€ï¸'}
          </button>
        </div>

        <div className="login-tabs">
          <button 
            className={`tab ${isLogin ? 'active' : ''}`}
            onClick={() => setIsLogin(true)}
          >
            Login
          </button>
          <button 
            className={`tab ${!isLogin ? 'active' : ''}`}
            onClick={() => setIsLogin(false)}
          >
            Register
          </button>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {error && (
            <div className="error-message">
              <span className="error-icon">âš ï¸</span>
              {error}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
              placeholder="driver@example.com"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              required
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
            />
          </div>

          {!isLogin && (
            <>
              <div className="form-group">
                <label htmlFor="name">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  placeholder="John Doe"
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                  placeholder="+1234567890"
                />
              </div>

              <div className="form-group">
                <label htmlFor="licenseNumber">Driver License Number</label>
                <input
                  type="text"
                  id="licenseNumber"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={handleInputChange}
                  required
                  placeholder="DL123456789"
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="vehicleModel">Vehicle Model</label>
                  <input
                    type="text"
                    id="vehicleModel"
                    name="vehicleModel"
                    value={formData.vehicleModel}
                    onChange={handleInputChange}
                    required
                    placeholder="Toyota Camry"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="vehicleColor">Vehicle Color</label>
                  <input
                    type="text"
                    id="vehicleColor"
                    name="vehicleColor"
                    value={formData.vehicleColor}
                    onChange={handleInputChange}
                    required
                    placeholder="White"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="vehicleType">Vehicle Type</label>
                  <select
                    id="vehicleType"
                    name="vehicleType"
                    value={formData.vehicleType}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="sedan">Sedan</option>
                    <option value="suv">SUV</option>
                    <option value="hatchback">Hatchback</option>
                    <option value="luxury">Luxury</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="plateNumber">License Plate</label>
                  <input
                    type="text"
                    id="plateNumber"
                    name="plateNumber"
                    value={formData.plateNumber}
                    onChange={handleInputChange}
                    required
                    placeholder="ABC-123"
                  />
                </div>
              </div>
            </>
          )}

          <button 
            type="submit" 
            className="login-btn"
            disabled={loading}
          >
            {loading ? (
              <div className="loading-spinner">ğŸ”„</div>
            ) : (
              <>
                <span>{isLogin ? 'Login' : 'Register'}</span>
                <span className="btn-arrow">â†’</span>
              </>
            )}
          </button>
        </form>

        <div className="demo-section">
          <div className="divider">
            <span>OR</span>
          </div>
          <button 
            type="button" 
            onClick={handleDemoLogin}
            className="demo-btn"
          >
            <span className="demo-icon">ğŸš€</span>
            Try Demo Mode
          </button>
        </div>

        <div className="login-footer">
          <p>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              type="button"
              className="switch-btn"
              onClick={() => setIsLogin(!isLogin)}
            >
              {isLogin ? "Register here" : "Login here"}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;

driver-app/src/components/Modal.css.content:
/* Modal Styles for Driver App */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  backdrop-filter: blur(4px);
}

.modal-content {
  background: white;
  border-radius: 16px;
  max-width: 500px;
  width: 90%;
  max-height: 80vh;
  overflow-y: auto;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  animation: modalSlideIn 0.2s ease-out;
}

@keyframes modalSlideIn {
  from {
    opacity: 0;
    transform: translate(-50%, -60%) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 24px 16px;
  border-bottom: 1px solid #e5e7eb;
}

.modal-header h3 {
  margin: 0;
  font-size: 18px;
  font-weight: 600;
  color: #111827;
}

.close-btn {
  background: none;
  border: none;
  font-size: 24px;
  color: #6b7280;
  cursor: pointer;
  width: 32px;
  height: 32px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
}

.close-btn:hover {
  background-color: #f3f4f6;
  color: #374151;
}

.modal-body {
  padding: 20px 24px;
}

.customer-info {
  background-color: #f9fafb;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 20px;
}

.customer-info p {
  margin: 4px 0;
  font-size: 14px;
  color: #4b5563;
}

.customer-info strong {
  color: #111827;
  font-weight: 600;
}

.otp-input-section {
  margin-bottom: 20px;
}

.otp-input-section label {
  display: block;
  font-weight: 500;
  color: #374151;
  margin-bottom: 8px;
}

.otp-input {
  width: 100%;
  padding: 12px 16px;
  font-size: 18px;
  text-align: center;
  letter-spacing: 8px;
  border: 2px solid #d1d5db;
  border-radius: 8px;
  transition: all 0.2s ease;
  font-family: 'Courier New', monospace;
}

.otp-input:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.otp-hint {
  margin-top: 8px;
  font-size: 12px;
  color: #6b7280;
  background-color: #fef3c7;
  padding: 8px 12px;
  border-radius: 6px;
  border-left: 4px solid #f59e0b;
}

.cancellation-reason-section {
  margin-bottom: 20px;
}

.cancellation-reason-section label {
  display: block;
  font-weight: 500;
  color: #374151;
  margin-bottom: 8px;
}

.cancellation-select {
  width: 100%;
  padding: 12px 16px;
  font-size: 14px;
  border: 2px solid #d1d5db;
  border-radius: 8px;
  background-color: white;
  transition: all 0.2s ease;
}

.cancellation-select:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.error-message {
  background-color: #fef2f2;
  border: 1px solid #fecaca;
  border-radius: 8px;
  padding: 12px;
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 16px;
}

.error-icon {
  font-size: 16px;
}

.error-message {
  color: #dc2626;
  font-size: 14px;
}

.modal-actions {
  padding: 16px 24px 24px;
  border-top: 1px solid #e5e7eb;
  display: flex;
  gap: 12px;
  justify-content: flex-end;
}

.btn {
  padding: 10px 20px;
  font-size: 14px;
  font-weight: 500;
  border-radius: 8px;
  border: none;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 6px;
  min-width: 120px;
  justify-content: center;
}

.btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.btn-secondary {
  background-color: #f3f4f6;
  color: #374151;
  border: 1px solid #d1d5db;
}

.btn-secondary:hover:not(:disabled) {
  background-color: #e5e7eb;
  border-color: #9ca3af;
}

.btn-primary {
  background-color: #3b82f6;
  color: white;
}

.btn-primary:hover:not(:disabled) {
  background-color: #2563eb;
}

.btn-danger {
  background-color: #dc2626;
  color: white;
}

.btn-danger:hover:not(:disabled) {
  background-color: #b91c1c;
}

/* Dark theme support */
body.dark .modal-content {
  background-color: #1f2937;
  color: #f9fafb;
}

body.dark .modal-header {
  border-bottom-color: #374151;
}

body.dark .modal-header h3 {
  color: #f9fafb;
}

body.dark .close-btn {
  color: #9ca3af;
}

body.dark .close-btn:hover {
  background-color: #374151;
  color: #d1d5db;
}

body.dark .customer-info {
  background-color: #374151;
}

body.dark .customer-info p {
  color: #d1d5db;
}

body.dark .customer-info strong {
  color: #f9fafb;
}

body.dark .otp-input,
body.dark .cancellation-select {
  background-color: #374151;
  border-color: #4b5563;
  color: #f9fafb;
}

body.dark .otp-input:focus,
body.dark .cancellation-select:focus {
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

body.dark .otp-hint {
  background-color: #451a03;
  color: #fdba74;
  border-left-color: #f59e0b;
}

body.dark .modal-actions {
  border-top-color: #374151;
}

body.dark .btn-secondary {
  background-color: #374151;
  color: #d1d5db;
  border-color: #4b5563;
}

body.dark .btn-secondary:hover:not(:disabled) {
  background-color: #4b5563;
  border-color: #6b7280;
}

/* Mobile responsiveness */
@media (max-width: 640px) {
  .modal-content {
    width: 95%;
    max-height: 90vh;
    margin: 16px;
  }

  .modal-actions {
    flex-direction: column;
  }

  .btn {
    min-width: auto;
  }
}

driver-app/src/pages/ActiveRides.js.content:
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import '../components/Modal.css';

// Fix Leaflet default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const ActiveRides = ({ appState }) => {
  const { activeRides, setActiveRides, driver, socket } = appState;

  // Single active ride for demo
  const [rides, setRides] = useState(() => {
    // Only show one active ride if not completed
    const sampleRide = {
      id: 'active_ride_1',
      customer: {
        name: 'Sarah Johnson',
        phone: '+1234567890',
        rating: 4.8,
        avatar: 'SJ'
      },
      pickup: {
        address: 'Downtown Shopping Mall, Main Street',
        coords: [40.7128, -74.0060]
      },
      drop: {
        address: 'JFK International Airport, Terminal 4',
        coords: [40.6413, -73.7781]
      },
      distance: '18.5 km',
      estimatedTime: '25 min',
      fare: 32.50,
      status: 'assigned',
      startTime: Date.now() - 300000, // 5 minutes ago
      bookingId: 'booking_123',
      customerId: 'customer_456'
    };

    // Return empty array if ride is completed, otherwise return single ride
    const isCompleted = localStorage.getItem(`ride_completed_${sampleRide.id}`) || 
                       localStorage.getItem(`ride_completed_${sampleRide.bookingId}`);

    return isCompleted ? [] : [sampleRide];
  });

  const [rideTimers, setRideTimers] = useState({});
  const [cancellationModal, setCancellationModal] = useState(null);
  const [cancellationReason, setCancellationReason] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [completedRideDetails, setCompletedRideDetails] = useState(null);
  const [showCompletionMap, setShowCompletionMap] = useState(false);
  const [completionStatus, setCompletionStatus] = useState(null);

  useEffect(() => {
    const timer = setInterval(() => {
      const newTimers = {};
      rides.forEach(ride => {
        const elapsed = Date.now() - ride.startTime;
        const minutes = Math.floor(elapsed / 60000);
        const seconds = Math.floor((elapsed % 60000) / 1000);
        newTimers[ride.id] = `${minutes}:${seconds.toString().padStart(2, '0')}`;
      });
      setRideTimers(newTimers);
    }, 1000);

    return () => clearInterval(timer);
  }, [rides]);

  const handleCall = (phoneNumber) => {
    // In a real app, this would trigger a phone call
    window.open(`tel:${phoneNumber}`);
  };

  const handleNavigate = (destination) => {
    // In a real app, this would open maps with navigation
    const [lat, lng] = destination;
    window.open(`https://www.google.com/maps/dir/?api=1&destination=${lat},${lng}`);
  };

  const handleStatusUpdate = (rideId, newStatus) => {
    setRides(prev => prev.map(ride => 
      ride.id === rideId 
        ? { ...ride, status: newStatus }
        : ride
    ));
  };

  // Direct ride completion - show map then complete
  const handleCompleteRide = async (ride) => {
    // First show the map with starting location
    setCompletedRideDetails(ride);
    setShowCompletionMap(true);
  };

  // Complete the ride from map view
  const finalizeRideCompletion = async () => {
    if (!completedRideDetails) return;

    const ride = completedRideDetails;
    setShowCompletionMap(false);
    await completeRide(ride);
  };

  // Complete the ride and update history
  const completeRide = async (ride) => {
    setLoading(true);
    try {
      // Update ride status in database
      const rideCompletionData = {
        bookingId: ride.bookingId,
        driverId: driver.id,
        customerId: ride.customerId,
        finalFare: ride.fare,
        status: 'completed',
        completedAt: new Date().toISOString(),
        paymentStatus: 'paid'
      };

      // Call API to complete ride
      const response = await axios.post(
        'http://localhost:3001/api/ride/complete',
        rideCompletionData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('driverToken')}`
          }
        }
      );

      if (response.data.success) {
        // Immediately remove from active rides to prevent re-appearance
        setRides(prev => prev.filter(r => r.id !== ride.id));

        // Update driver earnings
        const earnings = localStorage.getItem('driverEarnings') || '0';
        const newEarnings = parseFloat(earnings) + ride.fare;
        localStorage.setItem('driverEarnings', newEarnings.toString());

        // Save completed ride to history
        const completedRideRecord = {
          id: ride.id,
          bookingId: ride.bookingId,
          customerId: ride.customerId,
          customerName: ride.customer.name,
          customerPhone: ride.customer.phone,
          pickup: ride.pickup.address,
          drop: ride.drop.address,
          distance: ride.distance,
          fare: ride.fare,
          status: 'completed',
          completedAt: new Date().toISOString(),
          paymentStatus: 'paid'
        };

        // Save to localStorage for history
        const existingHistory = JSON.parse(localStorage.getItem('driverRideHistory') || '[]');
        existingHistory.unshift(completedRideRecord);
        localStorage.setItem('driverRideHistory', JSON.stringify(existingHistory));

        // Mark ride as permanently completed
        localStorage.setItem(`ride_completed_${ride.id}`, 'true');
        localStorage.setItem(`ride_completed_${ride.bookingId}`, 'true');

        // Set completion status
        setCompletionStatus({
          message: 'Ride Completed!',
          earnings: ride.fare
        });

        // Clear completion details and status after showing message
        setTimeout(() => {
          setCompletedRideDetails(null);
          setCompletionStatus(null);
        }, 3000);

        // Emit socket event for real-time updates to customer
        if (socket) {
          socket.emit('rideCompleted', {
            rideId: ride.id,
            bookingId: ride.bookingId,
            driverId: driver.id,
            customerId: ride.customerId,
            fare: ride.fare,
            completedAt: completedRideRecord.completedAt,
            customerName: ride.customer.name
          });
        }
      } else {
        setError(response.data.message || 'Failed to complete ride');
      }
    } catch (error) {
      console.error('Error completing ride:', error);
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Cancel ride with reason
  const cancelRideWithReason = async () => {
    if (!cancellationModal || !cancellationReason.trim()) {
      setError('Please provide a cancellation reason');
      return;
    }

    const ride = cancellationModal;
    setLoading(true);

    try {
      const cancellationData = {
        bookingId: ride.bookingId,
        driverId: driver.id,
        customerId: ride.customerId,
        reason: cancellationReason,
        status: 'cancelled',
        cancelledAt: new Date().toISOString(),
        cancelledBy: 'driver'
      };

      const response = await axios.post(
        'http://localhost:3001/api/ride/cancel',
        cancellationData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('driverToken')}`
          }
        }
      );

      if (response.data.success) {
        // Remove from active rides
        setRides(prev => prev.filter(r => r.id !== ride.id));

        setCancellationModal(null);
        setCancellationReason('');

        alert(`Ride cancelled successfully.\nReason: ${cancellationReason}\n\nCustomer has been notified.`);

        // Emit socket event
        if (socket) {
          socket.emit('rideCancelled', {
            rideId: ride.id,
            driverId: driver.id,
            customerId: ride.customerId,
            reason: cancellationReason
          });
        }
      } else {
        setError(response.data.message || 'Failed to cancel ride');
      }
    } catch (error) {
      console.error('Error cancelling ride:', error);
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getStatusInfo = (status) => {
    switch (status) {
      case 'assigned':
        return {
          label: 'Ride Assigned',
          icon: 'ğŸ¡',
          color: '#22c55e',
          bgColor: '#dcfce7'
        };
      case 'en_route_to_pickup':
        return {
          label: 'En Route to Pickup',
          icon: 'ğŸš—',
          color: '#3b82f6',
          bgColor: '#dbeafe'
        };
      case 'arrived_at_pickup':
        return {
          label: 'Arrived at Pickup',
          icon: 'ğŸ“',
          color: '#f59e0b',
          bgColor: '#fef3c7'
        };
      case 'passenger_on_board':
        return {
          label: 'Passenger On Board',
          icon: 'ğŸ‘¥',
          color: '#10b981',
          bgColor: '#d1fae5'
        };
      case 'en_route_to_destination':
        return {
          label: 'En Route to Destination',
          icon: 'ğŸ¯',
          color: '#8b5cf6',
          bgColor: '#e9d5ff'
        };
      default:
        return {
          label: 'Active',
          icon: 'ğŸš—',
          color: '#6b7280',
          bgColor: '#f3f4f6'
        };
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="dashboard-welcome">
          <h1>ğŸš— Active Rides</h1>
          <p className="dashboard-subtitle">
            Manage your ongoing trips and communicate with passengers.
          </p>
        </div>
      </div>

      {rides.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">ğŸš—</div>
          <h3 className="empty-title">No active rides</h3>
          <p className="empty-description">
            When you accept a ride request, it will appear here for you to manage.
          </p>
        </div>
      ) : (
        <div className="rides-section">
          {rides.map(ride => {
            const statusInfo = getStatusInfo(ride.status);

            return (
              <div key={ride.id} className="active-ride-card">
                {/* Ride Status Header */}
                <div className="ride-status" style={{ backgroundColor: statusInfo.bgColor }}>
                  <div className="status-info">
                    <span className="status-icon" style={{ color: statusInfo.color }}>
                      {statusInfo.icon}
                    </span>
                    <span className="status-label" style={{ color: statusInfo.color }}>
                      {statusInfo.label}
                    </span>
                  </div>
                  <div className="ride-timer">
                    <span>â±ï¸ {rideTimers[ride.id] || '0:00'}</span>
                  </div>
                </div>

                {/* Customer Information */}
                <div className="ride-header">
                  <div className="ride-customer">
                    <div className="customer-avatar">
                      {ride.customer.avatar}
                    </div>
                    <div className="customer-info">
                      <h4>{ride.customer.name}</h4>
                      <div className="customer-rating">
                        <span>â­ {ride.customer.rating}</span>
                        <span>ğŸ“ {ride.customer.phone}</span>
                      </div>
                    </div>
                  </div>
                  <div className="ride-price">
                    <h3 className="price-value">${ride.fare}</h3>
                    <p className="price-label">Trip Fare</p>
                  </div>
                </div>

                {/* Route Information */}
                <div className="ride-route">
                  <div className="route-item">
                    <span className="route-icon pickup-icon">ğŸ“</span>
                    <span className="route-address">{ride.pickup.address}</span>
                  </div>
                  <div className="route-item">
                    <span className="route-icon drop-icon">ğŸ¯</span>
                    <span className="route-address">{ride.drop.address}</span>
                  </div>
                </div>

                {/* Trip Details */}
                <div className="ride-meta">
                  <div className="meta-item">
                    <span>ğŸ“ {ride.distance}</span>
                  </div>
                  <div className="meta-item">
                    <span>â±ï¸ ~{ride.estimatedTime}</span>
                  </div>
                </div>

                {/* Action Buttons based on Status */}
                <div className="action-buttons">
                  {ride.status === 'assigned' ? (
                    // Only show Navigate and Complete Ride for assigned rides
                    <>
                      <button 
                        className="action-btn navigate-btn"
                        onClick={() => handleNavigate(ride.pickup.coords)}
                      >
                        ğŸ—ºï¸ Navigate
                      </button>

                      <button 
                        className="action-btn complete-btn"
                        style={{ background: '#10b981' }}
                        onClick={() => handleCompleteRide(ride)}
                      >
                        âœ… Complete Ride
                      </button>
                    </>
                  ) : (
                    // Other status buttons for non-assigned rides
                    <>
                      <button 
                        className="action-btn call-btn"
                        onClick={() => handleCall(ride.customer.phone)}
                      >
                        ğŸ“ Call Customer
                      </button>

                      <button 
                        className="action-btn navigate-btn"
                        onClick={() => handleNavigate(
                          ride.status === 'en_route_to_pickup' 
                            ? ride.pickup.coords 
                            : ride.drop.coords
                        )}
                      >
                        ğŸ—ºï¸ Navigate
                      </button>

                      {ride.status === 'en_route_to_pickup' && (
                        <button 
                          className="action-btn"
                          style={{ background: '#f59e0b' }}
                          onClick={() => handleStatusUpdate(ride.id, 'arrived_at_pickup')}
                        >
                          ğŸ“ Arrived at Pickup
                        </button>
                      )}

                      {ride.status === 'arrived_at_pickup' && (
                        <button 
                          className="action-btn"
                          style={{ background: '#10b981' }}
                          onClick={() => handleStatusUpdate(ride.id, 'passenger_on_board')}
                        >
                          ğŸ‘¥ Passenger Boarded
                        </button>
                      )}

                      {ride.status === 'passenger_on_board' && (
                        <button 
                          className="action-btn"
                          style={{ background: '#8b5cf6' }}
                          onClick={() => handleStatusUpdate(ride.id, 'en_route_to_destination')}
                        >
                          ğŸ¯ Start Trip
                        </button>
                      )}

                      {ride.status === 'en_route_to_destination' && (
                        <button 
                          className="action-btn complete-btn"
                          style={{ background: '#10b981' }}
                          onClick={() => handleCompleteRide(ride)}
                        >
                          âœ… Complete Ride
                        </button>
                      )}
                    </>
                  )}
                </div>

                {/* Emergency Actions */}
                <div className="emergency-actions">
                  <button 
                    className="emergency-btn"
                    onClick={() => alert('Emergency services contacted')}
                  >
                    ğŸš¨ Emergency
                  </button>

                  <button 
                    className="support-btn"
                    onClick={() => alert('Support team contacted')}
                  >
                    ğŸ“ Support
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* Quick Actions */}
      <div className="rides-section">
        <div className="section-header">
          <h2 className="section-title">âš¡ Quick Actions</h2>
        </div>

        <div className="quick-actions-grid">
          <button className="quick-action-card">
            <div className="action-icon">ğŸš¨</div>
            <h4>Emergency</h4>
            <p>Contact emergency services</p>
          </button>

          <button className="quick-action-card">
            <div className="action-icon">ğŸ“</div>
            <h4>Support</h4>
            <p>Get help from our team</p>
          </button>

          <button className="quick-action-card">
            <div className="action-icon">â›½</div>
            <h4>Gas Stations</h4>
            <p>Find nearby fuel stops</p>
          </button>

          <button className="quick-action-card">
            <div className="action-icon">ğŸ¥</div>
            <h4>Hospitals</h4>
            <p>Locate nearest hospital</p>
          </button>
        </div>
      </div>

      {/* Completion Map Modal */}
      {showCompletionMap && completedRideDetails && (
        <div className="modal-overlay" onClick={() => setShowCompletionMap(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>ğŸ—ºï¸ Starting Location</h3>
              <button 
                className="close-btn"
                onClick={() => setShowCompletionMap(false)}
              >
                Ã—
              </button>
            </div>

            <div className="modal-body">
              <p><strong>Customer:</strong> {completedRideDetails.customer.name}</p>
              <p><strong>Pickup:</strong> {completedRideDetails.pickup.address}</p>
              <p><strong>Fare:</strong> ${completedRideDetails.fare}</p>

              <div style={{ height: '300px', width: '100%', marginTop: '10px' }}>
                <MapContainer
                  center={completedRideDetails.pickup.coords}
                  zoom={13}
                  style={{ height: '100%', width: '100%' }}
                >
                  <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                  <Marker position={completedRideDetails.pickup.coords}>
                    <Popup>Pickup Location: {completedRideDetails.pickup.address}</Popup>
                  </Marker>
                </MapContainer>
              </div>
            </div>

            <div className="modal-actions">
              <button 
                className="btn btn-secondary"
                onClick={() => setShowCompletionMap(false)}
                disabled={loading}
              >
                Back
              </button>
              <button 
                className="btn btn-primary"
                onClick={finalizeRideCompletion}
                disabled={loading}
              >
                {loading ? 'ğŸ”„ Completing...' : 'âœ… Complete Ride'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Cancellation Modal */}
      {cancellationModal && (
        <div className="modal-overlay" onClick={() => setCancellationModal(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>âš ï¸ Cancel Ride</h3>
              <button 
                className="close-btn"
                onClick={() => setCancellationModal(null)}
              >
                Ã—
              </button>
            </div>

            <div className="modal-body">
              <div className="customer-info">
                <p><strong>Customer:</strong> {cancellationModal.customer.name}</p>
                <p><strong>Phone:</strong> {cancellationModal.customer.phone}</p>
              </div>

              <div className="cancellation-reason-section">
                <label htmlFor="cancellationReason">Reason for cancellation:</label>
                <select
                  id="cancellationReason"
                  value={cancellationReason}
                  onChange={(e) => setCancellationReason(e.target.value)}
                  className="cancellation-select"
                >
                  <option value="">Select a reason...</option>
                  <option value="Customer not found at pickup">Customer not found at pickup</option>
                  <option value="Customer cancelled">Customer cancelled</option>
                  <option value="Vehicle breakdown">Vehicle breakdown</option>
                  <option value="Emergency situation">Emergency situation</option>
                  <option value="Customer was intoxicated">Customer was intoxicated</option>
                  <option value="Unsafe pickup location">Unsafe pickup location</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              {error && (
                <div className="error-message">
                  <span className="error-icon">âš ï¸</span>
                  {error}
                </div>
              )}
            </div>

            <div className="modal-actions">
              <button 
                className="btn btn-secondary"
                onClick={() => setCancellationModal(null)}
                disabled={loading}
              >
                Back
              </button>
              <button 
                className="btn btn-danger"
                onClick={cancelRideWithReason}
                disabled={loading || !cancellationReason}
              >
                {loading ? 'ğŸ”„ Cancelling...' : 'âš ï¸ Confirm Cancellation'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Completion Status Modal */}
      {completionStatus && (
        <div className="modal-overlay">
          <div className="modal-content" style={{ textAlign: 'center', padding: '40px' }}>
            <div style={{ fontSize: '60px', marginBottom: '20px' }}>ğŸ‰</div>
            <h2 style={{ color: '#10b981', marginBottom: '10px' }}>{completionStatus.message}</h2>
            <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#111827' }}>
              Money Earned: ${completionStatus.earnings}
            </p>
            <div style={{ marginTop: '20px', fontSize: '16px', color: '#6b7280' }}>
              Ride has been removed from assigned section
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ActiveRides;

driver-app/src/pages/AvailableRides.js.content:
import React, { useState, useEffect } from 'react';

const AvailableRides = ({ appState }) => {
  const {
    availableRides,
    setAvailableRides,
    myBids,
    setMyBids,
    socket,
    driver,
    isOnline
  } = appState;

  const [bidAmounts, setBidAmounts] = useState({});
  const [bidding, setBidding] = useState({});

  // Mock available rides for demo
  const mockRides = [
    {
      id: 'ride_1',
      customer: {
        name: 'Sarah Johnson',
        rating: 4.8,
        totalRides: 127,
        avatar: 'SJ'
      },
      pickup: {
        address: 'Downtown Shopping Mall, Main Street',
        coords: [40.7128, -74.0060]
      },
      drop: {
        address: 'JFK International Airport, Terminal 4',
        coords: [40.6413, -73.7781]
      },
      distance: '18.5 km',
      estimatedTime: '25 min',
      suggestedPrice: 35,
      requestTime: '2 min ago',
      biddingEndsAt: Date.now() + 300000, // 5 minutes from now
      currentBids: [
        { driverId: 'driver_2', amount: 32, driverName: 'Mike Wilson', rating: 4.6 },
        { driverId: 'driver_3', amount: 30, driverName: 'Lisa Chen', rating: 4.9 }
      ]
    },
    {
      id: 'ride_2',
      customer: {
        name: 'Robert Kim',
        rating: 4.5,
        totalRides: 89,
        avatar: 'RK'
      },
      pickup: {
        address: 'Central University Campus, Building A',
        coords: [40.7589, -73.9851]
      },
      drop: {
        address: 'Financial District, Wall Street Plaza',
        coords: [40.7074, -74.0113]
      },
      distance: '12.3 km',
      estimatedTime: '18 min',
      suggestedPrice: 22,
      requestTime: '5 min ago',
      biddingEndsAt: Date.now() + 180000, // 3 minutes from now
      currentBids: [
        { driverId: 'driver_4', amount: 20, driverName: 'John Davis', rating: 4.7 }
      ]
    },
    {
      id: 'ride_3',
      customer: {
        name: 'Emma Thompson',
        rating: 4.9,
        totalRides: 203,
        avatar: 'ET'
      },
      pickup: {
        address: 'Sunset Beach Resort, Ocean Drive',
        coords: [40.7282, -74.0776]
      },
      drop: {
        address: 'Grand Central Station, East 42nd St',
        coords: [40.7527, -73.9772]
      },
      distance: '8.7 km',
      estimatedTime: '15 min',
      suggestedPrice: 18,
      requestTime: '1 min ago',
      biddingEndsAt: Date.now() + 420000, // 7 minutes from now
      currentBids: []
    }
  ];

  const [rides, setRides] = useState(mockRides);

  const handleBidSubmit = (rideId) => {
    const bidAmount = parseFloat(bidAmounts[rideId]);

    if (!bidAmount || bidAmount <= 0) {
      alert('Please enter a valid bid amount');
      return;
    }

    setBidding(prev => ({ ...prev, [rideId]: true }));

    // Simulate bid submission
    setTimeout(() => {
      const newBid = {
        id: Date.now(),
        rideId,
        driverId: driver.id,
        amount: bidAmount,
        driverName: driver.name,
        rating: driver.rating,
        timestamp: new Date(),
        status: 'pending'
      };

      setMyBids(prev => [...prev, newBid]);

      // Update the ride with the new bid
      setRides(prev => prev.map(ride => {
        if (ride.id === rideId) {
          return {
            ...ride,
            currentBids: [...ride.currentBids, {
              driverId: driver.id,
              amount: bidAmount,
              driverName: driver.name,
              rating: driver.rating
            }]
          };
        }
        return ride;
      }));

      setBidAmounts(prev => ({ ...prev, [rideId]: '' }));
      setBidding(prev => ({ ...prev, [rideId]: false }));

      // Emit bid to server if socket is connected
      if (socket) {
        socket.emit('placeBid', newBid);
      }
    }, 1500);
  };

  const handleBidAmountChange = (rideId, amount) => {
    setBidAmounts(prev => ({ ...prev, [rideId]: amount }));
  };

  const getTimeRemaining = (endTime) => {
    const now = Date.now();
    const timeLeft = endTime - now;

    if (timeLeft <= 0) return 'Expired';

    const minutes = Math.floor(timeLeft / 60000);
    const seconds = Math.floor((timeLeft % 60000) / 1000);

    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  const [timeRemaining, setTimeRemaining] = useState({});

  useEffect(() => {
    const timer = setInterval(() => {
      const newTimeRemaining = {};
      rides.forEach(ride => {
        newTimeRemaining[ride.id] = getTimeRemaining(ride.biddingEndsAt);
      });
      setTimeRemaining(newTimeRemaining);
    }, 1000);

    return () => clearInterval(timer);
  }, [rides]);

  const hasUserBid = (rideId) => {
    return rides.find(ride => ride.id === rideId)?.currentBids
      .some(bid => bid.driverId === driver.id);
  };

  const getUserBidAmount = (rideId) => {
    const ride = rides.find(ride => ride.id === rideId);
    const userBid = ride?.currentBids.find(bid => bid.driverId === driver.id);
    return userBid?.amount;
  };

  const getLowestBid = (currentBids) => {
    if (currentBids.length === 0) return null;
    return Math.min(...currentBids.map(bid => bid.amount));
  };

  if (!isOnline) {
    return (
      <div className="dashboard-container">
        <div className="empty-state">
          <div className="empty-icon">ğŸ”´</div>
          <h3 className="empty-title">You are offline</h3>
          <p className="empty-description">
            Go online from your dashboard to view and bid on available rides.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="dashboard-welcome">
          <h1>ğŸ” Available Rides</h1>
          <p className="dashboard-subtitle">
            Browse ride requests in your area and place competitive bids to win customers.
          </p>
        </div>
      </div>

      {rides.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">ğŸ”</div>
          <h3 className="empty-title">No rides available</h3>
          <p className="empty-description">
            New ride requests will appear here. Check back in a few moments!
          </p>
        </div>
      ) : (
        <div className="rides-section">
          {rides.map(ride => (
            <div key={ride.id} className="ride-card">
              {/* Ride Header */}
              <div className="ride-header">
                <div className="ride-customer">
                  <div className="customer-avatar">
                    {ride.customer.avatar}
                  </div>
                  <div className="customer-info">
                    <h4>{ride.customer.name}</h4>
                    <div className="customer-rating">
                      <span>â­ {ride.customer.rating}</span>
                      <span>({ride.customer.totalRides} rides)</span>
                    </div>
                  </div>
                </div>
                <div className="ride-price">
                  <p className="price-label">Suggested Fare</p>
                  <h3 className="price-value">${ride.suggestedPrice}</h3>
                </div>
              </div>

              {/* Route Information */}
              <div className="ride-route">
                <div className="route-item">
                  <span className="route-icon pickup-icon">ğŸ“</span>
                  <span className="route-address">{ride.pickup.address}</span>
                </div>
                <div className="route-item">
                  <span className="route-icon drop-icon">ğŸ¯</span>
                  <span className="route-address">{ride.drop.address}</span>
                </div>
              </div>

              {/* Ride Metadata */}
              <div className="ride-meta">
                <div className="meta-item">
                  <span>ğŸ“ {ride.distance}</span>
                </div>
                <div className="meta-item">
                  <span>â±ï¸ {ride.estimatedTime}</span>
                </div>
                <div className="meta-item">
                  <span>ğŸ•’ {ride.requestTime}</span>
                </div>
                <div className="meta-item" style={{ color: timeRemaining[ride.id] === 'Expired' ? '#ef4444' : '#f59e0b' }}>
                  <span>â° {timeRemaining[ride.id] || 'Loading...'}</span>
                </div>
              </div>

              {/* Current Bids */}
              {ride.currentBids.length > 0 && (
                <div className="current-bids">
                  <h5>Current Bids ({ride.currentBids.length})</h5>
                  <div className="bids-list">
                    {ride.currentBids
                      .sort((a, b) => a.amount - b.amount)
                      .slice(0, 3)
                      .map((bid, index) => (
                        <div 
                          key={bid.driverId} 
                          className={`bid-item ${bid.driverId === driver.id ? 'user-bid' : ''} ${index === 0 ? 'lowest-bid' : ''}`}
                        >
                          <span className="bid-driver">
                            {bid.driverId === driver.id ? 'You' : bid.driverName}
                            {index === 0 && <span className="lowest-badge">Lowest</span>}
                          </span>
                          <span className="bid-amount">${bid.amount}</span>
                          <span className="bid-rating">â­{bid.rating}</span>
                        </div>
                      ))}
                    {ride.currentBids.length > 3 && (
                      <div className="more-bids">
                        +{ride.currentBids.length - 3} more bids
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Bidding Section */}
              <div className="bid-section">
                {hasUserBid(ride.id) ? (
                  <div className="bid-placed">
                    <div className="bid-status">
                      <span className="status-icon">âœ…</span>
                      <span>Your bid: ${getUserBidAmount(ride.id)}</span>
                    </div>
                    <p className="suggested-price">
                      {getUserBidAmount(ride.id) === getLowestBid(ride.currentBids) 
                        ? 'ğŸ† You have the lowest bid!' 
                        : `ğŸ’¡ Current lowest bid: $${getLowestBid(ride.currentBids)}`
                      }
                    </p>
                  </div>
                ) : timeRemaining[ride.id] === 'Expired' ? (
                  <div className="bid-expired">
                    <span>âŒ Bidding has ended</span>
                  </div>
                ) : (
                  <div className="bid-input-container">
                    <input
                      type="number"
                      placeholder="Enter bid amount"
                      value={bidAmounts[ride.id] || ''}
                      onChange={(e) => handleBidAmountChange(ride.id, e.target.value)}
                      className="bid-input"
                      min="1"
                      step="0.50"
                    />
                    <button
                      onClick={() => handleBidSubmit(ride.id)}
                      disabled={bidding[ride.id] || !bidAmounts[ride.id]}
                      className="bid-btn"
                    >
                      {bidding[ride.id] ? (
                        <>
                          <span>ğŸ”„</span>
                          Placing...
                        </>
                      ) : (
                        <>
                          <span>ğŸ¯</span>
                          Place Bid
                        </>
                      )}
                    </button>
                  </div>
                )}

                {!hasUserBid(ride.id) && (
                  <div className="suggested-price">
                    ğŸ’¡ Suggested: ${ride.suggestedPrice} | 
                    {ride.currentBids.length > 0 
                      ? ` Beat lowest: $${getLowestBid(ride.currentBids) - 0.5}` 
                      : ' Be the first to bid!'
                    }
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Tips Section */}
      <div className="rides-section">
        <div className="section-header">
          <h2 className="section-title">ğŸ’¡ Bidding Tips</h2>
        </div>

        <div className="tips-grid">
          <div className="tip-card">
            <div className="tip-icon">ğŸ¯</div>
            <h4>Competitive Pricing</h4>
            <p>Bid competitively but fairly. Consider distance, traffic, and your operating costs.</p>
          </div>

          <div className="tip-card">
            <div className="tip-icon">âš¡</div>
            <h4>Quick Response</h4>
            <p>Respond quickly to ride requests. Customers often select the first few bids they receive.</p>
          </div>

          <div className="tip-card">
            <div className="tip-icon">â­</div>
            <h4>Build Reputation</h4>
            <p>Maintain high ratings to increase your chances of winning bids over other drivers.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AvailableRides;

driver-app/src/pages/History.js.content:
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const History = ({ appState }) => {
  const { driver } = appState;
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchHistory = async () => {
      setLoading(true);
      setError('');
      try {
        const res = await axios.get('http://localhost:3001/api/driver/history', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('driverToken')}`
          }
        });
        if (res.data.success) {
          setHistory(res.data.history);
        } else {
          setError(res.data.message || 'Failed to load history');
        }
      } catch (err) {
        setError('Network error loading history');
      } finally {
        setLoading(false);
      }
    };
    fetchHistory();
  }, []);

  if (loading) {
    return (
      <div className="dashboard-container">
        <p>Loading history...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="dashboard-welcome">
          <h1>ğŸ“‹ Ride History</h1>
          <p className="dashboard-subtitle">
            View your completed rides and customer feedback.
          </p>
        </div>
      </div>

      {error && (
        <div className="error-message" style={{ marginBottom: 16 }}>
          <span className="error-icon">âš ï¸</span>
          {error}
        </div>
      )}

      <div className="rides-section">
        {history.map(ride => (
          <div key={ride.id} className="ride-card">
            <div className="ride-header">
              <div className="ride-customer">
                <div className="customer-avatar">
                  {ride.customer?.name?.charAt(0) || '?'}
                </div>
                <div className="customer-info">
                  <h4>{ride.customer?.name || 'Customer'}</h4>
                  <div className="customer-rating">
                    <span>â­ {ride.customerRating || ride.customer?.rating || '-'}</span>
                    <span>{new Date(ride.date || ride.completedAt || Date.now()).toLocaleString()}</span>
                  </div>
                </div>
              </div>
              <div className="ride-price">
                <h3 className="price-value">${ride.fare?.toFixed ? ride.fare.toFixed(2) : ride.fare}</h3>
              </div>
            </div>

            <div className="ride-route">
              <div className="route-item">
                <span className="route-icon pickup-icon">ğŸ“</span>
                <span className="route-address">{ride.pickup}</span>
              </div>
              <div className="route-item">
                <span className="route-icon drop-icon">ğŸ¯</span>
                <span className="route-address">{ride.drop}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default History;

driver-app/src/pages/Home.js.content:
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = ({ appState }) => {
  const navigate = useNavigate();
  const {
    availableRides,
    activeRides,
    isOnline,
    setIsOnline,
    earnings,
    driver,
    socket,
    driverLocation,
    setDriverLocation,
    locationLoading,
    setLocationLoading,
    locationError,
    setLocationError,
    ReverseGeocode
  } = appState;

  const [stats, setStats] = useState({
    todayRides: 3,
    pendingBids: 2,
    totalEarnings: 245.50,
    rating: 4.8
  });

  const handleToggleOnline = () => {
    if (!isOnline && !driverLocation.coords) {
      // Get location before going online
      detectLocation();
      return;
    }

    const newStatus = !isOnline;
    setIsOnline(newStatus);

    if (socket) {
      socket.emit('driverStatusUpdate', {
        driverId: driver.id,
        isOnline: newStatus,
        location: driverLocation.coords
      });
    }
  };

  const detectLocation = () => {
    if (!navigator.geolocation) {
      setLocationError('Geolocation is not supported by this browser.');
      return;
    }

    setLocationLoading(true);
    setLocationError('');

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        const address = await ReverseGeocode(latitude, longitude);
        setDriverLocation({
          coords: [latitude, longitude],
          address
        });
        setLocationLoading(false);

        // Auto go online after getting location
        setIsOnline(true);
        if (socket) {
          socket.emit('driverStatusUpdate', {
            driverId: driver.id,
            isOnline: true,
            location: [latitude, longitude]
          });
        }
      },
      (error) => {
        setLocationLoading(false);
        switch (error.code) {
          case error.PERMISSION_DENIED:
            setLocationError('Location access denied. Please enable location services.');
            break;
          case error.POSITION_UNAVAILABLE:
            setLocationError('Location information unavailable.');
            break;
          case error.TIMEOUT:
            setLocationError('Location request timeout. Please try again.');
            break;
          default:
            setLocationError('An unknown error occurred while retrieving location.');
            break;
        }
      },
      { timeout: 10000, enableHighAccuracy: true }
    );
  };

  // Mock data for demo
  const recentRides = [
    {
      id: '1',
      passenger: 'Sarah Johnson',
      pickup: 'Downtown Mall',
      drop: 'Airport Terminal',
      fare: 25.50,
      rating: 5,
      time: '2 hours ago'
    },
    {
      id: '2',
      passenger: 'Mike Chen',
      pickup: 'University Campus',
      drop: 'Business District',
      fare: 18.75,
      rating: 4,
      time: '5 hours ago'
    }
  ];

  return (
    <div className="dashboard-container">
      {/* Dashboard Header */}
      <div className="dashboard-header">
        <div className="dashboard-welcome">
          <h1>Welcome back, {driver?.name || 'Driver'}! ğŸ‘‹</h1>
          <p className="dashboard-subtitle">
            Ready to start earning? Toggle your status to go online and receive ride requests.
          </p>
        </div>
      </div>

      {/* Online Status Toggle */}
      <div className="online-status-card">
        <div className="status-info">
          <div className={`status-icon ${isOnline ? 'online' : 'offline'}`}>
            {isOnline ? 'ğŸŸ¢' : 'ğŸ”´'}
          </div>
          <div className="status-text">
            <h3>{isOnline ? 'You are Online' : 'You are Offline'}</h3>
            <p className="status-description">
              {isOnline 
                ? 'Ready to receive ride requests' 
                : locationLoading 
                  ? 'Getting your location...' 
                  : 'Go online to start accepting rides'
              }
            </p>
            {locationError && (
              <div className="location-error-display">
                <span className="error-icon">âš ï¸</span>
                <span className="error-text">{locationError}</span>
              </div>
            )}
          </div>
        </div>

        <label className="toggle-switch">
          <input
            type="checkbox"
            checked={isOnline}
            onChange={handleToggleOnline}
            disabled={locationLoading}
          />
          <span className="slider"></span>
        </label>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-icon">ğŸš—</div>
            <h3 className="stat-title">Today's Rides</h3>
          </div>
          <div className="stat-value">{stats.todayRides}</div>
          <p className="stat-subtitle">+2 from yesterday</p>
        </div>

        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-icon">ğŸ’°</div>
            <h3 className="stat-title">Today's Earnings</h3>
          </div>
          <div className="stat-value">${stats.totalEarnings}</div>
          <p className="stat-subtitle">+$45.50 from yesterday</p>
        </div>

        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-icon">â­</div>
            <h3 className="stat-title">Rating</h3>
          </div>
          <div className="stat-value">{stats.rating}</div>
          <p className="stat-subtitle">Based on {driver?.totalRides || 245} rides</p>
        </div>

        <div className="stat-card">
          <div className="stat-header">
            <div className="stat-icon">ğŸ¯</div>
            <h3 className="stat-title">Pending Bids</h3>
          </div>
          <div className="stat-value">{stats.pendingBids}</div>
          <p className="stat-subtitle">Awaiting customer selection</p>
        </div>
      </div>

      {/* Available Rides Section */}
      {isOnline && (
        <div className="rides-section">
          <div className="section-header">
            <h2 className="section-title">ğŸ” Available Rides Near You</h2>
          </div>

          {availableRides.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">ğŸ”</div>
              <h3 className="empty-title">No rides available right now</h3>
              <p className="empty-description">
                New ride requests will appear here. Stay online to receive notifications!
              </p>
            </div>
          ) : (
            <div>
              {availableRides.slice(0, 3).map(ride => (
                <div key={ride.id} className="ride-card">
                  <div className="ride-header">
                    <div className="ride-customer">
                      <div className="customer-avatar">
                        {ride.customer?.name?.charAt(0) || 'U'}
                      </div>
                      <div className="customer-info">
                        <h4>{ride.customer?.name || 'Anonymous'}</h4>
                        <div className="customer-rating">
                          <span>â­ {ride.customer?.rating || 4.5}</span>
                          <span>({ride.customer?.totalRides || 50} rides)</span>
                        </div>
                      </div>
                    </div>
                    <div className="ride-price">
                      <p className="price-label">Suggested Fare</p>
                      <h3 className="price-value">${ride.suggestedPrice || 25}</h3>
                    </div>
                  </div>

                  <div className="ride-route">
                    <div className="route-item">
                      <span className="route-icon pickup-icon">ğŸ“</span>
                      <span className="route-address">{ride.pickup?.address || 'Pickup location'}</span>
                    </div>
                    <div className="route-item">
                      <span className="route-icon drop-icon">ğŸ¯</span>
                      <span className="route-address">{ride.drop?.address || 'Drop location'}</span>
                    </div>
                  </div>

                  <div className="ride-meta">
                    <div className="meta-item">
                      <span>ğŸ“ {ride.distance || '5.2'} km</span>
                    </div>
                    <div className="meta-item">
                      <span>â±ï¸ {ride.estimatedTime || '15'} min</span>
                    </div>
                    <div className="meta-item">
                      <span>ğŸ•’ {ride.requestTime || 'Just now'}</span>
                    </div>
                  </div>

                  <div className="action-buttons">
                    <button 
                      className="bid-btn"
                      onClick={() => navigate('/available-rides')}
                    >
                      Place Bid
                    </button>
                  </div>
                </div>
              ))}

              <div style={{ textAlign: 'center', marginTop: '20px' }}>
                <button 
                  className="bid-btn"
                  onClick={() => navigate('/available-rides')}
                  style={{ background: 'transparent', color: '#ff6b35', border: '2px solid #ff6b35' }}
                >
                  View All Available Rides â†’
                </button>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Active Rides Section */}
      {activeRides.length > 0 && (
        <div className="rides-section">
          <div className="section-header">
            <h2 className="section-title">ğŸš— Your Active Rides</h2>
          </div>

          {activeRides.map(ride => (
            <div key={ride.id} className="active-ride-card">
              <div className="ride-status">
                <span className="status-badge">EN ROUTE</span>
                <span className="ride-timer">Started 5 min ago</span>
              </div>

              <div className="ride-header">
                <div className="ride-customer">
                  <div className="customer-avatar">
                    {ride.customer?.name?.charAt(0) || 'U'}
                  </div>
                  <div className="customer-info">
                    <h4>{ride.customer?.name || 'Anonymous'}</h4>
                    <div className="customer-rating">
                      <span>â­ {ride.customer?.rating || 4.5}</span>
                    </div>
                  </div>
                </div>
                <div className="ride-price">
                  <h3 className="price-value">${ride.acceptedBid || 25}</h3>
                </div>
              </div>

              <div className="ride-route">
                <div className="route-item">
                  <span className="route-icon pickup-icon">ğŸ“</span>
                  <span className="route-address">{ride.pickup?.address}</span>
                </div>
                <div className="route-item">
                  <span className="route-icon drop-icon">ğŸ¯</span>
                  <span className="route-address">{ride.drop?.address}</span>
                </div>
              </div>

              <div className="action-buttons">
                <button className="action-btn call-btn">
                  ğŸ“ Call
                </button>
                <button className="action-btn navigate-btn">
                  ğŸ—ºï¸ Navigate
                </button>
                <button className="action-btn complete-btn">
                  âœ… Complete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Recent Rides */}
      <div className="rides-section">
        <div className="section-header">
          <h2 className="section-title">ğŸ“‹ Recent Rides</h2>
        </div>

        {recentRides.map(ride => (
          <div key={ride.id} className="ride-card" style={{ opacity: 0.8 }}>
            <div className="ride-header">
              <div className="ride-customer">
                <div className="customer-avatar">
                  {ride.passenger.charAt(0)}
                </div>
                <div className="customer-info">
                  <h4>{ride.passenger}</h4>
                  <div className="customer-rating">
                    <span>â­ {ride.rating}</span>
                    <span>{ride.time}</span>
                  </div>
                </div>
              </div>
              <div className="ride-price">
                <h3 className="price-value">${ride.fare}</h3>
              </div>
            </div>

            <div className="ride-route">
              <div className="route-item">
                <span className="route-icon pickup-icon">ğŸ“</span>
                <span className="route-address">{ride.pickup}</span>
              </div>
              <div className="route-item">
                <span className="route-icon drop-icon">ğŸ¯</span>
                <span className="route-address">{ride.drop}</span>
              </div>
            </div>
          </div>
        ))}

        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button 
            className="bid-btn"
            onClick={() => navigate('/history')}
            style={{ background: 'transparent', color: '#ff6b35', border: '2px solid #ff6b35' }}
          >
            View All History â†’
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;

driver-app-vite/src/components/Login.css.content:
/* Login Container */
.login-container {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  padding: 20px;
  font-family: 'Inter', sans-serif;
}

body.dark .login-container {
  background: linear-gradient(135deg, #d63031 0%, #e17055 100%);
}

.login-card {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 40px;
  width: 100%;
  max-width: 500px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

body.dark .login-card {
  background: rgba(42, 42, 42, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

/* Login Header */
.login-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 30px;
}

.brand-section {
  text-align: left;
}

.brand-icon {
  font-size: 3rem;
  margin-bottom: 10px;
}

.brand-section h1 {
  margin: 0 0 8px 0;
  font-size: 2rem;
  font-weight: 700;
  color: #1f2937;
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

body.dark .brand-section h1 {
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.brand-section p {
  margin: 0;
  color: #6b7280;
  font-size: 1rem;
  font-weight: 400;
}

body.dark .brand-section p {
  color: #9ca3af;
}

.theme-toggle {
  background: rgba(255, 107, 53, 0.1);
  border: 2px solid rgba(255, 107, 53, 0.2);
  border-radius: 50%;
  width: 50px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.theme-toggle:hover {
  background: rgba(255, 107, 53, 0.2);
  transform: scale(1.1);
}

body.dark .theme-toggle {
  background: rgba(255, 107, 53, 0.15);
  border-color: rgba(255, 107, 53, 0.3);
}

/* Login Tabs */
.login-tabs {
  display: flex;
  margin-bottom: 30px;
  background: #f3f4f6;
  border-radius: 12px;
  padding: 4px;
}

body.dark .login-tabs {
  background: #4b5563;
}

.tab {
  flex: 1;
  padding: 12px 20px;
  background: transparent;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  color: #6b7280;
  cursor: pointer;
  transition: all 0.3s ease;
}

.tab.active {
  background: white;
  color: #ff6b35;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

body.dark .tab {
  color: #9ca3af;
}

body.dark .tab.active {
  background: #1f2937;
  color: #ff6b35;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
}

/* Login Form */
.login-form {
  margin-bottom: 30px;
}

.form-group {
  margin-bottom: 20px;
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 15px;
}

@media (max-width: 480px) {
  .form-row {
    grid-template-columns: 1fr;
    gap: 20px;
  }
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  font-weight: 600;
  color: #374151;
  font-size: 0.9rem;
}

body.dark .form-group label {
  color: #d1d5db;
}

.form-group input,
.form-group select {
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e5e7eb;
  border-radius: 10px;
  font-size: 1rem;
  transition: all 0.3s ease;
  background: white;
  box-sizing: border-box;
}

.form-group input:focus,
.form-group select:focus {
  outline: none;
  border-color: #ff6b35;
  box-shadow: 0 0 0 3px rgba(255, 107, 53, 0.1);
}

body.dark .form-group input,
body.dark .form-group select {
  background: #374151;
  border-color: #4b5563;
  color: #f9fafb;
}

body.dark .form-group input:focus,
body.dark .form-group select:focus {
  border-color: #ff6b35;
  box-shadow: 0 0 0 3px rgba(255, 107, 53, 0.15);
}

/* Error Message */
.error-message {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  background: #fef2f2;
  border: 1px solid #fecaca;
  border-radius: 8px;
  color: #dc2626;
  font-size: 0.9rem;
  margin-bottom: 20px;
}

body.dark .error-message {
  background: #2d1b1b;
  border-color: #8b4949;
  color: #fca5a5;
}

.error-icon {
  font-size: 1.1rem;
}

/* Login Button */
.login-btn {
  width: 100%;
  padding: 15px 20px;
  background: linear-gradient(135deg, #ff6b35 0%, #f7931e 100%);
  color: white;
  border: none;
  border-radius: 12px;
  font-size: 1.1rem;
  font-weight: 700;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  box-shadow: 0 4px 15px rgba(255, 107, 53, 0.3);
}

.login-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(255, 107, 53, 0.4);
}

.login-btn:disabled {
  opacity: 0.7;
  cursor: not-allowed;
  transform: none;
}

.loading-spinner {
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.btn-arrow {
  font-size: 1.2rem;
  transition: transform 0.3s ease;
}

.login-btn:hover .btn-arrow {
  transform: translateX(4px);
}

/* Demo Section */
.demo-section {
  margin-bottom: 20px;
}

.divider {
  position: relative;
  text-align: center;
  margin: 20px 0;
}

.divider::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: 1px;
  background: #e5e7eb;
}

body.dark .divider::before {
  background: #4b5563;
}

.divider span {
  background: white;
  padding: 0 15px;
  color: #6b7280;
  font-size: 0.9rem;
  font-weight: 500;
}

body.dark .divider span {
  background: #2a2a2a;
  color: #9ca3af;
}

.demo-btn {
  width: 100%;
  padding: 12px 20px;
  background: rgba(255, 107, 53, 0.1);
  color: #ff6b35;
  border: 2px solid rgba(255, 107, 53, 0.2);
  border-radius: 10px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
}

.demo-btn:hover {
  background: rgba(255, 107, 53, 0.15);
  transform: translateY(-1px);
}

body.dark .demo-btn {
  background: rgba(255, 107, 53, 0.15);
  border-color: rgba(255, 107, 53, 0.3);
  color: #ff6b35;
}

body.dark .demo-btn:hover {
  background: rgba(255, 107, 53, 0.2);
}

.demo-icon {
  font-size: 1.2rem;
}

/* Login Footer */
.login-footer {
  text-align: center;
}

.login-footer p {
  margin: 0;
  color: #6b7280;
  font-size: 0.9rem;
}

body.dark .login-footer p {
  color: #9ca3af;
}

.switch-btn {
  background: none;
