import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Login = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    licenseNumber: '',
    vehicleModel: '',
    vehicleColor: '',
    vehicleType: 'sedan',
    plateNumber: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'light');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const endpoint = isLogin ? '/api/driver/login' : '/api/driver/register';
      const response = await axios.post(`http://localhost:3001${endpoint}`, formData);

      if (response.data.success) {
        localStorage.setItem('driverToken', response.data.token);
        localStorage.setItem('driver', JSON.stringify(response.data.driver));
        onLogin(response.data.driver);
      } else {
        setError(response.data.message || 'Authentication failed');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Network error occurred');
      console.error('Auth error:', err);
    } finally {
      setLoading(false);
    }
  };

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    document.body.className = newTheme;
    localStorage.setItem('theme', newTheme);
  };

  // Demo login with sample data
  const handleDemoLogin = () => {
    const demoDriver = {
      id: 'demo_driver_1',
      name: 'John Driver',
      email: 'john@example.com',
      phone: '+1234567890',
      licenseNumber: 'DL123456789',
      vehicleModel: 'Toyota Camry',
      vehicleColor: 'White',
      vehicleType: 'sedan',
      plateNumber: 'ABC-123',
      rating: 4.8,
      totalRides: 245,
      isOnline: false,
      avatar: 'JD'
    };

    localStorage.setItem('driverToken', 'demo_token');
    localStorage.setItem('driver', JSON.stringify(demoDriver));
    onLogin(demoDriver);
  };

  React.useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="brand-section">
            <div className="brand-icon">ğŸš—</div>
            <h1>BidCab Driver</h1>
            <p>Start earning with every ride</p>
          </div>

          <button 
            className="theme-toggle"
            onClick={toggleTheme}
            title={`Switch to ${theme === 'light' ? 'dark' : 'light'} mode`}
          >
            {theme === 'light' ? 'ğŸŒ™' : 'â˜€ï¸'}
          </button>
        </div>

        <div className="login-tabs">
          <button 
            className={`tab ${isLogin ? 'active' : ''}`}
            onClick={() => setIsLogin(true)}
          >
            Login
          </button>
          <button 
            className={`tab ${!isLogin ? 'active' : ''}`}
            onClick={() => setIsLogin(false)}
          >
            Register
          </button>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {error && (
            <div className="error-message">
              <span className="error-icon">âš ï¸</span>
              {error}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
              placeholder="driver@example.com"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              required
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
            />
          </div>

          {!isLogin && (
            <>
              <div className="form-group">
                <label htmlFor="name">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  placeholder="John Doe"
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                  placeholder="+1234567890"
                />
              </div>

              <div className="form-group">
                <label htmlFor="licenseNumber">Driver License Number</label>
                <input
                  type="text"
                  id="licenseNumber"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={handleInputChange}
                  required
                  placeholder="DL123456789"
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="vehicleModel">Vehicle Model</label>
                  <input
                    type="text"
                    id="vehicleModel"
                    name="vehicleModel"
                    value={formData.vehicleModel}
                    onChange={handleInputChange}
                    required
                    placeholder="Toyota Camry"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="vehicleColor">Vehicle Color</label>
                  <input
                    type="text"
                    id="vehicleColor"
                    name="vehicleColor"
                    value={formData.vehicleColor}
                    onChange={handleInputChange}
                    required
                    placeholder="White"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="vehicleType">Vehicle Type</label>
                  <select
                    id="vehicleType"
                    name="vehicleType"
                    value={formData.vehicleType}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="sedan">Sedan</option>
                    <option value="suv">SUV</option>
                    <option value="hatchback">Hatchback</option>
                    <option value="luxury">Luxury</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="plateNumber">License Plate</label>
                  <input
                    type="text"
                    id="plateNumber"
                    name="plateNumber"
                    value={formData.plateNumber}
                    onChange={handleInputChange}
                    required
                    placeholder="ABC-123"
                  />
                </div>
              </div>
            </>
          )}

          <button 
            type="submit" 
            className="login-btn"
            disabled={loading}
          >
            {loading ? (
              <div className="loading-spinner">ğŸ”„</div>
            ) : (
              <>
                <span>{isLogin ? 'Login' : 'Register'}</span>
                <span className="btn-arrow">â†’</span>
              </>
            )}
          </button>
        </form>

        <div className="demo-section">
          <div className="divider">
            <span>OR</span>
          </div>
          <button 
            type="button" 
            onClick={handleDemoLogin}
            className="demo-btn"
          >
            <span className="demo-icon">ğŸš€</span>
            Try Demo Mode
          </button>
        </div>

        <div className="login-footer">
          <p>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              type="button"
              className="switch-btn"
              onClick={() => setIsLogin(!isLogin)}
            >
              {isLogin ? "Register here" : "Login here"}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;

driver-app-vite/src/components/Modal.css.content:
/* Modal Styles for Driver App */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  backdrop-filter: blur(4px);
}

.modal-content {
  background: white;
  border-radius: 16px;
  max-width: 500px;
  width: 90%;
  max-height: 80vh;
  overflow-y: auto;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  animation: modalSlideIn 0.2s ease-out;
}

@keyframes modalSlideIn {
  from {
    opacity: 0;
    transform: translate(-50%, -60%) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 24px 16px;
  border-bottom: 1px solid #e5e7eb;
}

.modal-header h3 {
  margin: 0;
  font-size: 18px;
  font-weight: 600;
  color: #111827;
}

.close-btn {
  background: none;
  border: none;
  font-size: 24px;
  color: #6b7280;
  cursor: pointer;
  width: 32px;
  height: 32px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
}

.close-btn:hover {
  background-color: #f3f4f6;
  color: #374151;
}

.modal-body {
  padding: 20px 24px;
}

.customer-info {
  background-color: #f9fafb;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 20px;
}

.customer-info p {
  margin: 4px 0;
  font-size: 14px;
  color: #4b5563;
}

.customer-info strong {
  color: #111827;
  font-weight: 600;
}

.otp-input-section {
  margin-bottom: 20px;
}

.otp-input-section label {
  display: block;
  font-weight: 500;
  color: #374151;
  margin-bottom: 8px;
}

.otp-input {
  width: 100%;
  padding: 12px 16px;
  font-size: 18px;
  text-align: center;
  letter-spacing: 8px;
  border: 2px solid #d1d5db;
  border-radius: 8px;
  transition: all 0.2s ease;
  font-family: 'Courier New', monospace;
}

.otp-input:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.otp-hint {
  margin-top: 8px;
  font-size: 12px;
  color: #6b7280;
  background-color: #fef3c7;
  padding: 8px 12px;
  border-radius: 6px;
  border-left: 4px solid #f59e0b;
}

.cancellation-reason-section {
  margin-bottom: 20px;
}

.cancellation-reason-section label {
  display: block;
  font-weight: 500;
  color: #374151;
  margin-bottom: 8px;
}

.cancellation-select {
  width: 100%;
  padding: 12px 16px;
  font-size: 14px;
  border: 2px solid #d1d5db;
  border-radius: 8px;
  background-color: white;
  transition: all 0.2s ease;
}

.cancellation-select:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.error-message {
  background-color: #fef2f2;
  border: 1px solid #fecaca;
  border-radius: 8px;
  padding: 12px;
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 16px;
}

.error-icon {
  font-size: 16px;
}

.error-message {
  color: #dc2626;
  font-size: 14px;
}

.modal-actions {
  padding: 16px 24px 24px;
  border-top: 1px solid #e5e7eb;
  display: flex;
  gap: 12px;
  justify-content: flex-end;
}

.btn {
  padding: 10px 20px;
  font-size: 14px;
  font-weight: 500;
  border-radius: 8px;
  border: none;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 6px;
  min-width: 120px;
  justify-content: center;
}

.btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.btn-secondary {
  background-color: #f3f4f6;
  color: #374151;
  border: 1px solid #d1d5db;
}

.btn-secondary:hover:not(:disabled) {
  background-color: #e5e7eb;
  border-color: #9ca3af;
}

.btn-primary {
  background-color: #3b82f6;
  color: white;
}

.btn-primary:hover:not(:disabled) {
  background-color: #2563eb;
}

.btn-danger {
  background-color: #dc2626;
  color: white;
}

.btn-danger:hover:not(:disabled) {
  background-color: #b91c1c;
}

/* Dark theme support */
body.dark .modal-content {
  background-color: #1f2937;
  color: #f9fafb;
}

body.dark .modal-header {
  border-bottom-color: #374151;
}

body.dark .modal-header h3 {
  color: #f9fafb;
}

body.dark .close-btn {
  color: #9ca3af;
}

body.dark .close-btn:hover {
  background-color: #374151;
  color: #d1d5db;
}

body.dark .customer-info {
  background-color: #374151;
}

body.dark .customer-info p {
  color: #d1d5db;
}

body.dark .customer-info strong {
  color: #f9fafb;
}

body.dark .otp-input,
body.dark .cancellation-select {
  background-color: #374151;
  border-color: #4b5563;
  color: #f9fafb;
}

body.dark .otp-input:focus,
body.dark .cancellation-select:focus {
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

body.dark .otp-hint {
  background-color: #451a03;
  color: #fdba74;
  border-left-color: #f59e0b;
}

body.dark .modal-actions {
  border-top-color: #374151;
}

body.dark .btn-secondary {
  background-color: #374151;
  color: #d1d5db;
  border-color: #4b5563;
}

body.dark .btn-secondary:hover:not(:disabled) {
  background-color: #4b5563;
  border-color: #6b7280;
}

/* Mobile responsiveness */
@media (max-width: 640px) {
  .modal-content {
    width: 95%;
    max-height: 90vh;
    margin: 16px;
  }

  .modal-actions {
    flex-direction: column;
  }

  .btn {
    min-width: auto;
  }
}

